import React, { Component } from 'react';

import UsersCard from './components/Users/UsersCard';
import KPIs from './components/KPIs/KPIs';
import Employees from './components/Employees/Employees';

import {
   Tabs,
   Tab,
   Button,
   H2,
   Position,
   Menu,
   MenuItem,
   Popover
} from '@blueprintjs/core';

export default class AdminPanel extends Component {
   state = {
      activeTabID: 'Users',
      selectedTabId: 'Users'
   };
   handleTabChange = activeTabID => {
      this.setState({ activeTabID: activeTabID });
   };

   render() {
      const addMenu = (
         <Menu>
            <MenuItem icon='user' text='Add User'></MenuItem>
         </Menu>
      );

      return (
         <div className='bg-transparent'>
            <div className=''>
               <H2>Admin Panel</H2>
            </div>

            <br></br>
            <Tabs
               onChange={this.handleTabChange}
               selectedTabId={this.state.selectedTabID}
               large
            >
               <Tab id='Users' title='Users' panel={<UsersCard />} />
               <Tab id='Employees' title='Employees' panel={<Employees />} />
               <Tab id='KPIs' title='KPIs' panel={<KPIs />} />
               <Tabs.Expander />
               <Popover content={addMenu} position={Position.BOTTOM}>
                  <Button icon='add' rightIcon='arrow-down' minimal />
               </Popover>
            </Tabs>
         </div>
      );
   }
}
