import React, { Component } from 'react';
import CardWithModal from '../../../components/Card/CardWithModal';
import LineChart from '../../../components/Charts/ApexChart/LineChart';

export default class ChartsBuilder extends Component {
   state = {
      labels: [1, 2, 3, 4, 5, 6, 6, 78, 8, 8, 90],

      series: [
         {
            name: 'series-1',
            data: [30, 40, 45, 50, 49, 60, 70, 91]
         }
      ]
   };
   renderKPIs() {
      const { data } = this.props;
      data.map(data => {
         return (
            <CardWithModal headerText='KPI_Name'>
               <LineChart
                  labels={data.map(d => {
                     return d.reports.map(r => {
                        return r.reportedAt;
                     });
                  })}
                  yaxisLabel='Results'
                  xasisLabel='Reports'
                  series={data.map(d => {
                     return d.reports.map(r => {
                        return r.result;
                     });
                  })}
                  targetValue={data.target}
                  targetInfo='KPI target'
               />
            </CardWithModal>
         );
      });
   }

   render() {
      return <div>{this.renderKPIs()}</div>;
   }
}

ChartsBuilder.defaultProps = {
   data: []
};
