import React, { Component } from 'react';
import {
   HTMLTable,
   Button,
   NonIdealState,
   H5,
   Spinner,
   ContextMenu,
   Dialog,
   Icon,
   Card
} from '@blueprintjs/core';
import Client from '../../../../Modules/Client/Client';
import EmployeeForm from './EmployeeForm';
import CardHeader from '../../../../components/Card/CardHeader';
import CardBody from '../../../../components/Card/CardBody';
import TableContextMenu from './TableContextMenu';

export default class UsersTable extends Component {
   state = {
      errorMsg: '',
      showEditCard: false,
      userForEdit: {}
   };
   isEligable() {
      let role = Client.User.getRole();
      if (role === 'ROOT') return false;
      if (role === 'ADMIN') return false;
      return true;
   }
   resetErrorMsg() {
      this.setState({
         errorMsg: ''
      });
   }
   async deleteEmployee(id) {
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.EmployeesService.deleteEmployeeById(id);
         if (result.status === 200) {
            Client.Notifications.success('Employee removed successfully');
         }
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error, cannot delete Employee');
      } finally {
         this.props.onRefresh();
      }
   }
   editEmployee(employee) {
      this.setState({ showEditCard: true, userForEdit: employee });
   }
   closeEditUser() {
      this.setState({ showEditCard: false });
   }
   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th>SAP</th>
               <th>Name</th>
               <th>Email</th>
               <th>Role</th>
               <th>Support Type</th>
               <th>Project</th>
               <th>Language</th>
               <th>RM</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      const { employees } = this.props;
      let emp = new Array();
      if (emp.length === 0) return 'no data to be displayd';
      return employees.map(emp => {
         return (
            <tr key={emp._id} onContextMenu={e => this.handeClick(e, emp)}>
               <td>{emp.sap}</td>
               <td>{emp.name}</td>
               <td>{emp.email}</td>
               <td>{emp.role}</td>
               <td>{emp.supportType}</td>
               <td>{emp.project.name}</td>
               <td>{emp.mainLanguage}</td>
               <td>{emp.rm.name}</td>
            </tr>
         );
      });
   }
   handeClick(e, emp) {
      e.preventDefault();
      ContextMenu.show(this.renderContextMenu(e, emp), {
         left: e.clientX,
         top: e.clientY
      });
   }
   renderContextMenu(e, emp) {
      return (
         <TableContextMenu
            user={emp}
            isEligable={this.isEligable()}
            onEdit={() => this.editUser(emp)}
            onRemove={() => this.deleteUser(emp._id)}
         />
      );
   }
   renderContent() {
      if (this.state.errorMsg) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>Something went wrong</strong>
               </H5>
               <i>Cannot load users table</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => this.getData()}
               ></Button>
            </NonIdealState>
         );
      }

      if (this.props.employees.length === 0) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>There are no Employees in database </strong>
               </H5>
               <i>No data to be displayed</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={this.props.onRefresh}
               ></Button>
            </NonIdealState>
         );
      }

      return (
         <div>
            <HTMLTable fill='true' condensed striped interactive>
               {this.renderTableHeader()}
               {this.renderTableBody()}
            </HTMLTable>
         </div>
      );
   }
   render() {
      const { userForEdit } = this.state;
      const { isLoading } = this.props;

      if (isLoading) {
         return <Spinner intent='primary'></Spinner>;
      }

      return (
         <div>
            {this.renderContent()}
            <Dialog
               isOpen={this.state.showEditCard}
               onClose={() => this.closeEditUser()}
               canEscapeKeyClose={true}
            >
               <Card>
                  <CardHeader>
                     <Icon icon='user' className='header-icon' />
                     Edit User
                     <div className='float-right'>
                        <Icon
                           icon='cross'
                           onClick={() => this.closeEditUser()}
                        />
                     </div>
                  </CardHeader>
                  <CardBody className='card-body'>
                     <EmployeeForm
                        username={userForEdit.username}
                        email={userForEdit.email}
                        role={userForEdit.role}
                        buttonCaption='Update'
                     />
                  </CardBody>
               </Card>
            </Dialog>
         </div>
      );
   }
}
UsersTable.defaultProps = {
   employees: [],
   onRefresh: function() {
      return;
   }
};
