import React, { Component } from 'react';
import {
   Modal,
   Button,
   ButtonGroup,
   Card,
   CardBody,
   CardHeader
} from 'reactstrap';
import StdCard from '../../../../components/Card/StdCard';
import CreataeEmployeeForm from '../../../../components/Forms/ContactTargetForms/CreateContactTargetForm';
//import EmployeeTable from '../../../../components/Table/EmployeeTable/EmployeeTable';
import axios from 'axios';

export default class EmployeeCard extends Component {
   constructor(props) {
      super(props);
      this.toggle = this.toggle.bind(this);
      this.state = {
         theme: this.props.theme,
         modal: false,
         ops: [],
         sms: []
      };
   }

   componentDidMount() {
      this.getSms();
      this.getOps();
   }

   getSms() {
      const URL = 'http://localhost:3001/api/employees/role/Service%20Manager';
      axios
         .get(URL)
         .then(res => {
            this.setState({
               sms: res.data
            });
         })
         .catch(err => {
            console.error('Somethig went wrong with loading SM list');
         });
   }

   getOps() {
      const URL =
         'http://localhost:3001/api/employees/role/Operations%20Manager';
      axios
         .get(URL)
         .then(res => {
            this.setState({
               ops: res.data
            });
         })
         .catch(err => {
            console.error('Somethig went wrong with loading OPS list');
         });
   }

   toggle() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }

   setContextInfo() {
      return 'Manage your Employees';
   }

   render() {
      const { theme, modal } = this.state;

      return (
         <StdCard icon='fas fa-users' headerText='Employee Manager' size='xl'>
            <div>
               <h5>
                  {this.setContextInfo()}
                  <ButtonGroup className='float-right'>
                     <Button size='sm' color='primary' onClick={this.toggle}>
                        <span className='fas fa-user-plus' />
                     </Button>
                  </ButtonGroup>
               </h5>
               <hr />
            </div>
            <Modal
               isOpen={modal}
               toggle={this.toggle}
               className='modal-full modal-my'
            >
               <Card>
                  <CardHeader>
                     <span class='fas fa-chart-line header-icon' />
                     {'Create new Employee'}
                     <span
                        className='far fa-window-restore float-right header-icon text-secondary'
                        onClick={this.toggle}
                     />
                  </CardHeader>

                  <CardBody className='card-body'>
                     <CreataeEmployeeForm theme={theme} />
                  </CardBody>
               </Card>
            </Modal>
         </StdCard>
      );
   }
}
