import React, { Component } from 'react';
import { Input, FormText } from 'reactstrap';
import axios from 'axios';

export default class RoleOptions extends Component {
   state = {
      roleArr: [],
      role: ''
   };
   componentDidMount() {
      this.getOptions();
   }
   getOptions() {
      const URL = '/api/employees/roles/all';
      axios
         .get(URL)
         .then(res => {
            this.setState({
               roleArr: res.data,
               role: res.data[0]._id
            });
         })
         .catch(err => {
            alert('Could not load employee roles');
            console.log(JSON.stringify(err));
         });
   }
   renderOptions() {
      return this.state.roleArr.map((option, key) => {
         return (
            <option key={key} value={option._id}>
               {option.role}
            </option>
         );
      });
   }
   render() {
      return (
         <div>
            <Input
               type='select'
               name={this.props.name}
               id={this.props.name}
               ref={this.props.name}
               size='sm'
               onChange={this.props.onChange}
               className={this.props.className}
               disabled={this.props.disabled}
            >
               <option
                  selected={this.props.value === '' ? true : false}
                  hidden
                  disabled
               >
                  Please Select Role
               </option>
               {this.renderOptions()}
            </Input>
            <FormText>Please provide Role</FormText>
         </div>
      );
   }
}
