import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import '@fortawesome/fontawesome-free'
import './table.css'

import { Table, Input, Button } from 'reactstrap'
import Select from '../Buttons/Select/Select'

export default class InputTable extends Component {

   state = {
      theme: this.props.theme,
      data: this.props.data,
      options: [{ value: 1, name: 'Analyst' }, { value: 1, name: 'Specialist' }],
      _testDataSet: [
         { id: 1, usertype: 'adm', name: 'RC', age: 29, email: 'rafal.cymbalista@hcl.com' },
         { id: 2, usertype: 'adm', name: 'RC', age: 29, email: 'rafal.cymbalista@hcl.com' }
      ]
   }

   setTheme(theme){
      if(theme === 'dark'){
         return 'table-dark'
      }
      if(theme === 'light'){
         return ''
      }
   }
   setInputTheme(theme){
      if(theme === 'dark'){
         return 'table-input-dark'
      }
      if(theme === 'light'){
         return ''
      }
   }

   renderTableHeaders() {
      let header = Object.keys(this.state._testDataSet[0])
      return header.map((key, index) => {
         return <th key={index}>{key.toUpperCase()}</th>
      })
   }
   //TODO:
   // -- create editable table step
   //under dev
   renderTableEditablebuttons() {
      return (
         <div>
            <td>
               <Button color="primary" size="sm"><i className="fas fa-edit"></i></Button>
            </td>
            <td>
               <Button color="danger" size="sm"><i className="fas fa-trash"></i></Button>
            </td>
         </div>
      )
   }

   renderTableData() {
      return this.state._testDataSet.map((user, index) => {
         const { id, name, age, email } = user;
         return (
            <tr key={id}>
               <td>{id}</td>
               <td>
                  <Select theme={this.state.theme} options={this.state.options}></Select>
               </td>
               <td><Input className={this.setInputTheme(this.state.theme)} size="sm" placeholder={name} /></td>
               <td><Input className={this.setInputTheme(this.state.theme)} size="sm" placeholder={age} /></td>
               <td><Input className={this.setInputTheme(this.state.theme)} size="sm" placeholder={email} /></td>
            </tr>
         )
      })
   }

   render() {
      return (
         <div>
            <Table responsive borderless striped hover size="sm" className={this.setTheme(this.state.theme)}>
               <thead>
                  {this.renderTableHeaders()}
               </thead>
               <tbody>
                  {this.renderTableData()}
               </tbody>
            </Table>
         </div>
      )
   }
}
