import React, { Component } from 'react';
import {
   HTMLTable,
   Button,
   NonIdealState,
   H5,
   Spinner,
   Card
} from '@blueprintjs/core';

export default class ProjectsTable extends Component {
   state = {
      errorMsg: '',
      showEditCard: false,
      projectForEdit: {},
      selected: ''
   };

   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th className='text-center pl-3'>#</th>
               <th>Proejct Name</th>
               <th>Manager</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      const { data } = this.props;
      return data.map((project, key) => {
         return (
            <tr
               key={project._id}
               onClick={e => this.handleSelection(e, project)}
               className={`pl-2 ${
                  this.state.selected._id === project._id ? 'bg-selected' : ''
               }`}
            >
               <td className='text-center pl-3'>{key}</td>
               <td style={{ paddingLeft: '20px' }}>{project.name}</td>
               <td>{project.manager.name}</td>
            </tr>
         );
      });
   }

   handleSelection(e, project) {
      e.preventDefault();
      this.props.onSelection(project, false);
      this.setState({
         selected: project
      });
   }

   renderContent() {
      if (this.state.errorMsg) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>Something went wrong</strong>
               </H5>
               <i>Cannot load Projects table</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => {
                     this.props.onRefresh();
                     this.setState({
                        errorMsg: ''
                     });
                  }}
               ></Button>
            </NonIdealState>
         );
      }
      return (
         <div className='overflow-x-scroll'>
            <HTMLTable fill='true' striped condensed interactive>
               {this.renderTableHeader()}
               {this.renderTableBody()}
            </HTMLTable>
         </div>
      );
   }
   render() {
      const { isLoading } = this.props;

      if (isLoading) {
         return <Spinner intent='primary'></Spinner>;
      }
      return <Card elevation='2'>{this.renderContent()}</Card>;
   }
}
ProjectsTable.defaultProps = {
   data: [],
   onRefresh: function() {
      return;
   }
};
