import React, { Component } from 'react';
import { Alert, H5, Portal } from '@blueprintjs/core';
import EmployeeForm from './EmployeeForm';
import Client from '../../../../Modules/Client/Client';
import DataManagerCard from '../../../../components/Card/DataManagerCard';
import EmployeesTable from './EmployeesTable';

export default class EmployeesCard extends Component {
   state = {
      sap: '',
      name: '',
      email: '',
      role: '',
      supportType: '',
      project: '',
      mainlanguage: '',
      rm: '',
      employees: [],
      isLoading: 'false',
      openAlert: false,
      errorMessage: '',
      errorMsg: ''
   };

   componentDidMount() {
      this.getData();
   }

   async getData() {
      this.setLoading(true);
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.EmployeesService.getEmployees();
         this.setState({
            employees: result.data
         });
         console.log(this.state.employees);
      } catch (err) {
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading Employees list');
      } finally {
         this.setLoading(false);
      }
   }
   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }
   resetErrorMsg() {
      this.setState({
         errorMsg: ''
      });
   }
   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   openAlert(msg) {
      this.setState({ openAlert: true, errorMessage: msg });
   }
   closeAlert() {
      this.setState({ openAlert: false, errorMessage: '' });
   }

   clearForm() {
      this.setState({
         sap: '',
         name: '',
         email: '',
         role: '',
         supportType: '',
         project: '',
         mainlanguage: '',
         rm: '',
         openAlert: false,
         errorMessage: ''
      });
   }

   handleSubmit = async e => {
      e.preventDefault();
      const {
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm
      } = this.state;
      var errorMsg = 'Unknown Error';
      let emp = Client.Services.EmployeesService.createEmployeeDTO(
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm
      );
      try {
         let result = await Client.Services.UsersService.saveNewUser(emp);
         if (result.status === 200) {
            Client.Notifications.success(`User ${name} successfully saved`);
            this.getData();
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${name} employee`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };

   render() {
      const {
         isLoading,
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm,
         errorMessage,
         openAlert,
         employees
      } = this.state;
      return (
         <DataManagerCard
            icon='th'
            cardSize='m'
            headerText='Users'
            cardCaption='All app users'
            addButtonText='Add new user'
            id='allUsers'
            modalHeaderText='Add new user'
            onRefresh={() => this.getData()}
            table={
               <EmployeesTable
                  employees={employees}
                  onRefresh={() => this.getData()}
                  isLoading={isLoading}
               />
            }
            addForm={
               <EmployeeForm
                  onChange={this.onChange}
                  onSubmit={this.handleSubmit}
                  sap={sap}
                  name={name}
                  email={email}
                  role={role}
                  supportType={supportType}
                  project={project}
                  mainlanguage={mainlanguage}
                  rm={rm}
                  buttonCaption='Create'
                  onClear={() => this.clearForm()}
               />
            }
         >
            <Alert
               isOpen={openAlert}
               confirmButtonText='Clear form'
               onConfirm={() => this.clearForm()}
               cancelButtonText='Cancel'
               onCancel={() => this.closeAlert()}
               intent='danger'
               icon='issue'
               ussePortal={
                  <Portal container={document.getElementById('allUsers')} />
               }
            >
               <H5>{errorMessage}</H5>
               <strong>Do you want to clear the data?</strong>
            </Alert>
         </DataManagerCard>
      );
   }
}
