import React, { Component } from 'react';
import './Sidebar.css';

export default class Sidebar extends Component {
   state = {
      selected: 1
   };

   changeSelection(id) {
      this.setState({ selected: id });
   }

   render() {
      return (
         <nav className='side-bar no-border bg-blur'>{this.props.children}</nav>
      );
   }
}
