import React, { Component } from 'react';
import { Menu, MenuItem } from '@blueprintjs/core';

export default class TableContextMenu extends Component {
   render() {
      const { language, onEdit, onRemove, isEligable } = this.props;
      return (
         <Menu>
            <MenuItem
               icon='translate'
               text={`${language.language} `}
               disabled
            />
            <Menu.Divider />
            <div hidden={isEligable}>
               <MenuItem
                  icon='edit'
                  text='Edit language'
                  onClick={onEdit}
                  disabled={isEligable}
               />
               <MenuItem
                  icon='remove'
                  text='Remove language'
                  onClick={onRemove}
                  disabled={isEligable}
               />
            </div>
            <MenuItem icon='properties' text='Show more...' />
         </Menu>
      );
   }
}
