import React, { Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import {
   Navbar,
   Button,
   Alignment,
   Menu,
   MenuItem,
   MenuDivider,
   Popover,
   Position,
   Dialog,
   Classes
} from '@blueprintjs/core';
import MainMenu from './components/MainMenu';
import UIManager from '../../Modules/UIManager/UIManager';
import Client from '../../Modules/Client/Client';
import './AppBar.css';

export default class AppBar extends Component {
   constructor(props) {
      super(props);
      this.state = {
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark',
         logout: false,
         goHome: false,
         isOpen: false
      };
      this.switchTheme = this.switchTheme.bind(this);
      this.handleLogout = this.handleLogout.bind(this);
   }

   handleOpen() {
      this.setState({ isOpen: true });
   }
   handleClose() {
      this.setState({ isOpen: false });
   }

   switchTheme() {
      UIManager.swtichTheme();
      UIManager.setChartView();
      this.setState({
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark'
      });
   }

   handleLogout = e => {
      e.preventDefault();
      Client.logout();
      this.setState({
         logout: true
      });
   };

   render() {
      if (this.state.logout) {
         return <Redirect to='/login' />;
      }

      var rightMenu = (
         <Menu>
            <Link to='/UserProfile'>
               <MenuItem icon='user' text='Profile' />
            </Link>
            <Link to='/Settings'>
               <MenuItem icon='cog' text='Settings...' />
            </Link>
            <MenuItem
               icon={this.state.themeIcon}
               text={this.state.themeToSwitch + ' mode'}
               onClick={this.switchTheme}
            />
            <MenuDivider />
            <MenuItem
               icon='log-out'
               text='Logout...'
               onClick={this.handleLogout}
            ></MenuItem>
         </Menu>
      );

      var mainMenu = (
         <div>
            <Menu>
               <MenuItem
                  icon='application'
                  text='About this app...'
                  onClick={() => this.handleOpen()}
               />
               <MenuItem
                  icon='applications'
                  text='Apps'
                  popoverProps={{ openOnTargetFocus: false }}
                  shouldDismissPopover={true}
               >
                  <Link to='/Dashboards'>
                     <MenuItem icon='dashboard' text='Dashboard' />
                  </Link>
                  <Link to='/Projects'>
                     <MenuItem icon='projects' text='Project' />
                  </Link>
                  <Link to='/Reports'>
                     <MenuItem icon='manually-entered-data' text='Reports' />
                  </Link>
                  <Link to='/AdminPanel'>
                     <MenuItem icon='control' text='Admin' />
                  </Link>
               </MenuItem>
            </Menu>
         </div>
      );

      return (
         <Navbar fixedToTop className='no-border border-bottom-0 pl-0'>
            <Navbar.Group align={Alignment.LEFT} className='adjust-nav-group '>
               <Popover
                  content={mainMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button minimal icon='circle' intent='primary'></Button>
               </Popover>
               <Navbar.Divider />
               <Link to='/Home'>
                  <Button minimal icon='home'></Button>
               </Link>
            </Navbar.Group>
            <Navbar.Group align={Alignment.RIGHT}>
               <Popover
                  content={rightMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button className='bp3-minimal' icon='property' text='' />
               </Popover>
            </Navbar.Group>
            <Dialog
               icon='info-sign'
               isOpen={this.state.isOpen}
               title='Palantir Foundry'
               autoFocus
               canEscapeKeyClose
               canOutsideClickClose
               enforceFocus
               usePortal
            >
               <div className={Classes.DIALOG_BODY}>
                  <p>
                     <strong>
                        Data integration is the seminal problem of the digital
                        age. For over ten years, we’ve helped the world’s
                        premier organizations rise to the challenge.
                     </strong>
                  </p>
                  <p>
                     Palantir Foundry radically reimagines the way enterprises
                     interact with data by amplifying and extending the power of
                     data integration. With Foundry, anyone can source, fuse,
                     and transform data into any shape they desire. Business
                     analysts become data engineers — and leaders in their
                     organization’s data revolution.
                  </p>
                  <p>
                     Foundry’s back end includes a suite of best-in-class data
                     integration capabilities: data provenance, git-style
                     versioning semantics, granular access controls, branching,
                     transformation authoring, and more. But these powers are
                     not limited to the back-end IT shop.
                  </p>
                  <p>
                     In Foundry, tables, applications, reports, presentations,
                     and spreadsheets operate as data integrations in their own
                     right. Access controls, transformation logic, and data
                     quality flow from original data source to intermediate
                     analysis to presentation in real time. Every end product
                     created in Foundry becomes a new data source that other
                     users can build upon. And the enterprise data foundation
                     goes where the business drives it.
                  </p>
                  <p>
                     Start the revolution. Unleash the power of data integration
                     with Palantir Foundry.
                  </p>
               </div>
            </Dialog>
         </Navbar>
      );
   }
}
