import React, { Component } from 'react';
import { Row } from 'reactstrap';
import { Alert, H5, Portal } from '@blueprintjs/core';
import Client from '../../../../Modules/Client/Client';
import DataManagerCard from '../../../../components/Card/DataManagerCard';
import LanguagesTable from './LanguagessTable';
import EmployeeLanguageForm from '../../../../components/Forms/EmployeeForms/EmployeeLanguageForm';

export default class LanguagesCard extends Component {
   state = {
      language: '',
      languages: [],
      isLoading: 'false',
      openAlert: false,
      errorMessage: '',
      errorMsg: ''
   };

   componentDidMount() {
      this.getData();
   }

   async getData() {
      this.setLoading(true);
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.LanguageService.getLanguages();
         this.setState({
            languages: result.data
         });
         console.log(result.data);
      } catch (err) {
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading languages list');
      } finally {
         this.setLoading(false);
      }
   }
   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }
   resetErrorMsg() {
      this.setState({
         errorMsg: ''
      });
   }
   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   openAlert(msg) {
      this.setState({ openAlert: true, errorMessage: msg });
   }
   closeAlert() {
      this.setState({ openAlert: false, errorMessage: '' });
   }

   clearForm() {
      this.setState({
         language: '',
         openAlert: false,
         errorMessage: ''
      });
   }

   handleSubmit = async e => {
      e.preventDefault();
      const { language } = this.state;
      var errorMsg = 'Unknown Error';
      let lang = Client.Services.LanguageService.createLanguageDTO(language);
      try {
         let result = await Client.Services.EmployeeRolesService.createEmployeeRoles(
            lang
         );
         if (result.status === 200) {
            Client.Notifications.success(
               `Language ${language} successfully saved`
            );
            this.getData();
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${language} language`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };

   render() {
      const {
         isLoading,
         language,
         errorMessage,
         openAlert,
         languages
      } = this.state;
      return (
         <Row>
            <DataManagerCard
               icon='th'
               cardSize='xl'
               headerText='Languages'
               cardCaption='All Languages'
               addButtonText='Add new language'
               id='allLanguages'
               modalHeaderText='Add new employee role'
               onRefresh={() => this.getData()}
               table={<div></div>}
               addForm={
                  <EmployeeLanguageForm
                     onChange={this.onChange}
                     onSubmit={this.handleSubmit}
                     language={language}
                     buttonCaption='Save'
                     onClear={() => this.clearForm()}
                  />
               }
            >
               <Alert
                  isOpen={openAlert}
                  confirmButtonText='Clear form'
                  onConfirm={() => this.clearForm()}
                  cancelButtonText='Cancel'
                  onCancel={() => this.closeAlert()}
                  intent='danger'
                  icon='issue'
                  ussePortal={
                     <Portal
                        container={document.getElementById('allLanguages')}
                     />
                  }
               >
                  <H5>{errorMessage}</H5>
                  <strong>Do you want to clear the data?</strong>
               </Alert>
            </DataManagerCard>
         </Row>
      );
   }
}
