import React, { Component } from 'react';
import { Row } from 'reactstrap';
import ScoreCard from '../ScoreCard/ScoreCard';
import './QuickView.css';
import axios from 'axios';

export default class Quick extends Component {
   state = {
      dataError: '',
      headcount: {
         headcount: 0,
         headcountProc: 0,
         headcountTrend: 0,
         eHeadcount: 0,
         eHeadcountProc: 0,
         eHeadcountTrend: 0
      },
      productivity: {
         productivity: 0,
         productivityProc: 0,
         productivityTrend: 0,
         eProductivity: 0,
         eProductivityProc: 0,
         eProductivityTrend: 0
      },
      staff: {
         availability: 0,
         availabilityProc: 0,
         availabilityTrend: 0,
         shrinkage: 0,
         shrinkageProc: 0,
         shrinkageTrend: 0
      }
   };

   componentDidMount() {
      this.getAPIdata();
   }

   getAPIdata() {
      axios
         .get('/api/prodsummary')
         .then(res =>
            this.setState({
               headcount: res.data.headcount,
               productivity: res.data.productivity,
               staff: res.data.staff
            })
         )
         .catch(error => {
            this.setState({
               dataError: 'error'
            });
            console.log(error);
         });
   }

   render() {
      return (
         <div>
            <Row>
               <ScoreCard
                  icon='fa fa-users'
                  title='Headcount'
                  procValue={this.state.headcount.headcountProc}
                  numberValue={this.state.headcount.headcount}
                  trend={this.state.headcount.headcountTrend}
               />
               <ScoreCard
                  icon='fa fa-user'
                  title='E.Headcount'
                  procValue={this.state.headcount.eHeadcountProc}
                  numberValue={this.state.headcount.eHeadcount}
                  trend={this.state.headcount.eHeadcountTrend}
               />
               <ScoreCard
                  icon='fas fa-chart-line'
                  title='Productivity'
                  procValue={this.state.productivity.productivityProc}
                  numberValue={this.state.productivity.productivity}
                  trend={this.state.productivity.productivityTrend}
               />
               <ScoreCard
                  icon='fas fa-chart-line'
                  title='E. Productivity'
                  procValue={this.state.productivity.eProductivityProc}
                  numberValue={this.state.productivity.eProductivity}
                  trend={this.state.productivity.productivityTrend}
               />
               <ScoreCard
                  icon='fa fa-clock'
                  title='Availability'
                  procValue={this.state.staff.availabilityProc}
                  numberValue={this.state.staff.availability}
                  trend={this.state.staff.availabilityTrend}
               />
               <ScoreCard
                  icon='fas fa-stopwatch'
                  title='Shrinkage'
                  procValue={this.state.staff.shrinkageProc}
                  numberValue={this.state.staff.shrinkage}
                  trend={this.state.staff.shrinkageTrend}
               />
            </Row>
         </div>
      );
   }
}
