import React, { Component } from 'react';
import {
   Button,
   ButtonGroup,
   Modal,
   Card,
   CardBody,
   CardHeader
} from 'reactstrap';
import StdCard from '../StdCard';
import CreateProjectFrom from '../../Forms/ProjectForms/CreateProjectForm/CreateProjectForm';
import EditProjectForm from '../../Forms/ProjectForms/EditProjectForm/EditProjectForm';

export default class ProjectCard extends Component {
   constructor(props) {
      super(props);
      this.toggleModal = this.toggleModal.bind(this);
      this.state = {
         theme: this.props.theme,
         modal: false,
         sms: [],
         ops: []
      };
   }

   toggleModal() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }

   setContextInfo() {
      return 'Manage your Projects';
   }

   setCardTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-dark text-white';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const { theme, modal } = this.state;

      return (
         <StdCard
            theme={theme}
            icon='fas fa-project-diagram'
            headerText='Projects Manager'
            size='xl'
         >
            <div>
               <h5>
                  {this.setContextInfo()}
                  <ButtonGroup className='float-right'>
                     <Button
                        size='sm'
                        color='primary'
                        onClick={() => {
                           this.toggleModal();
                        }}
                     >
                        <span className='fas fa-plus' />
                     </Button>
                  </ButtonGroup>
               </h5>
               <hr />
               <EditProjectForm theme={theme} />
               <Modal
                  isOpen={modal}
                  toggle={this.toggleModal}
                  className='modal-full modal-my'
               >
                  <Card className={this.setCardTheme()}>
                     <CardHeader>
                        <span class='fas fa-chart-line header-icon' />
                        {'Create new Project'}
                        <span
                           className='far fa-window-restore float-right header-icon text-secondary'
                           onClick={this.toggleModal}
                        />
                     </CardHeader>

                     <CardBody className='card-body'>
                        <CreateProjectFrom theme={theme} />
                     </CardBody>
                  </Card>
               </Modal>
            </div>
         </StdCard>
      );
   }
}
