import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CardSubtitle, CardBody, CardText, Col } from 'reactstrap';
import { Card, Icon } from '@blueprintjs/core';
import './IconCard.css';

const pStyle = {
   'min-height': '150px'
};

/*TODO:
 
*/
export default class IconeCard extends Component {
   state = {
      disable: this.props.disable,
      name: 'N/A',
      icon: '',
      proc: 0,
      number: 0
   };

   getTrend = value => {
      if (value < 0) return '-';
      else if (value > 0) return '+';
      else return ' ';
   };

   render() {
      const { title, icon, disable } = this.props;

      return (
         <Col xl='2' lg='2' md='3' sm='4' xs='12' className='mt-4'>
            <Card className={`text-center`}>
               <CardBody style={pStyle} className={`text-center`}>
                  <CardText>
                     <h1 className='mt-3 '>
                        <Link to={this.props.link}>
                           <div className='icon-card-icon'>
                              <Icon icon={icon} iconSize={45} />
                           </div>
                        </Link>
                     </h1>
                  </CardText>
                  <CardSubtitle>{title}</CardSubtitle>
               </CardBody>
            </Card>
         </Col>
      );
   }
}
