import React, { Component } from 'react';
import {
   HTMLTable,
   Button,
   NonIdealState,
   H5,
   Spinner,
   Card,
   Icon,
   Classes
} from '@blueprintjs/core';

export default class employeesTable extends Component {
   state = {
      errorMsg: '',
      showEditCard: false,
      employeeForEdit: {},
      selected: ''
   };

   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th className='text-center pl-3'>#</th>
               <th>Employee Name</th>
               <th>Email</th>
               <th className='text-center'>Is employee</th>
               <th className='text-center'>employee Type</th>
               <th className='text-center'>Project</th>
               <th className='text-center'>Team</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      const { data } = this.props;
      return data.map((employee, key) => {
         return (
            <tr
               key={employee._id}
               onClick={e => this.handleSelection(e, employee)}
               className={`pl-2 ${
                  this.state.selected._id === employee._id ? 'bg-selected' : ''
               }`}
            >
               <td className='text-center pl-3'>{key}</td>
               <td>{employee.name}</td>
               <td>{employee.email}</td>
               <td className='text-center'>
                  {employee.user !== null ? (
                     <Icon icon='tick' intent='success' />
                  ) : (
                     <Icon icon='small-cross' />
                  )}
               </td>
               <td className='text-center'>{employee.type.type}</td>
               <td className='text-center'>
                  {employee.project.name === null
                     ? 'N/A'
                     : employee.project.name}
               </td>
               <td className='text-center'>{employee.team.name}</td>
            </tr>
         );
      });
   }

   handleSelection(e, employee) {
      e.preventDefault();
      this.props.onSelection(employee, false);
      this.setState({
         selected: employee
      });
   }

   renderNonIdealState(header, information = '') {
      return (
         <div className='card-body'>
            <NonIdealState icon='issue'>
               <H5>
                  <strong>{header}</strong>
               </H5>
               <i className={Classes.TEXT_MUTED}>{information}</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => {
                     this.props.onRefresh();
                     this.setState({
                        errorMsg: ''
                     });
                  }}
               ></Button>
            </NonIdealState>
         </div>
      );
   }

   renderContent() {
      if (this.state.errorMsg) {
         this.renderNonIdealState(
            'Something went wrong',
            'Cannot load employees table'
         );
      }

      //if (this.props.data.length === 0) {
      //   return this.renderNonIdealState('There are no Employees in database');
      //}

      return (
         <div className='overflow-x-scroll'>
            <HTMLTable fill='true' striped condensed interactive>
               {this.renderTableHeader()}
               {this.renderTableBody()}
            </HTMLTable>
         </div>
      );
   }

   render() {
      const { isLoading } = this.props;

      if (isLoading) {
         return <Spinner intent='primary'></Spinner>;
      }
      return <Card elevation='2'>{this.renderContent()}</Card>;
   }
}
employeesTable.defaultProps = {
   data: [],
   onRefresh: function() {
      return;
   }
};
