import React, { Component } from 'react';
import { Row } from 'reactstrap';
import AppBar from '../../components/AppBar/AppBar';
import MainContainer from '../../components/MainContainer/MainContainer';
import IconCard from '../../components/Card/IconCard/IconCard';
import StdCard from '../../components/Card/StdCard';

import UIManager from '../../Modules/UIManager/UIManager';

export default class Home extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme()
      };
   }

   render() {
      return (
         <MainContainer>
            <AppBar></AppBar>
            <div className='container-fluid'>
               <Row>
                  <IconCard
                     icon='fas fa-chart-line'
                     theme={this.state.theme}
                     title='Dashboard'
                     link='/MainDashboard/'
                  />
                  <IconCard
                     icon='fas fa-project-diagram'
                     theme={this.state.theme}
                     title='Projects'
                     link='/Project/'
                  />
                  <IconCard
                     icon='fas fa-file-invoice'
                     theme={this.state.theme}
                     title='Report'
                     link='/Report/'
                  />
                  <IconCard
                     icon='fas fa-cog'
                     theme={this.state.theme}
                     title='Admin Panel'
                     link='AdminPanel'
                  />
               </Row>
               <Row>
                  <StdCard
                     theme={this.state.theme}
                     icon='fas fa-rss'
                     headerText='Project info'
                     size='xl'
                  >
                     <h3> Project info </h3>
                     <hr />
                     <h5>current version: 0.0.1</h5>
                     <hr />
                     <h5>Author: Rafal Cymbalista</h5>
                  </StdCard>
               </Row>
            </div>
         </MainContainer>
      );
   }
}
