import React, { Component } from 'react';
import {
   Tabs,
   Tab,
   Button,
   H2,
   ButtonGroup,
   Divider,
   Alert,
   Portal,
   H5,
   Classes,
   Icon
} from '@blueprintjs/core';
import UsersTable from './components/Users/UsersTable';
import Client from '../../Modules/Client/Client';
import UserDialog from './components/Users/UserDialog';

export default class AdminPanel extends Component {
   state = {
      activeTabID: 'Users',
      selectedTabId: 'Users',
      isLoading: true,
      users: [],
      openAlert: true,
      errorMessage: '',
      isUserDialogOpen: false,
      username: '',
      email: '',
      password: '',
      usertype: '',
      selected: {},
      isUserEditDialogOpen: false,
      isDeleteAlertOpen: false
   };

   getSelected = sel => {
      this.setState({
         selected: sel
      });
   };

   componentDidMount() {
      this.getUsersData();
   }

   async getUsersData() {
      this.setLoading(true);
      var result;
      try {
         //this.resetErrorMsg();
         result = await Client.Services.UsersService.getUsers();
         this.setState({
            users: result.data
         });
      } catch (err) {
         alert(err);
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading users list');
      } finally {
         this.setLoading(false);
      }
   }

   async deleteUser(id) {
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.UsersService.deleteUser(id);
         if (result.status === 200) {
            Client.Notifications.success('User removed successfully');
         }
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error, cannot delete user');
      } finally {
         this.props.onRefresh();
      }
   }

   handleSubmit = async e => {
      e.preventDefault();
      const { email, username, usertype, password } = this.state;
      var errorMsg = 'Unknown Error';
      let usr = Client.Services.UsersService.createUserDTO(
         username,
         email,
         password,
         usertype
      );
      try {
         let result = await Client.Services.UsersService.saveNewUser(usr);
         if (result.status === 200) {
            Client.Notifications.success(`User ${username} successfully saved`);
            this.getUsersData();
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${username} user`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };
   openAlert(msg) {
      this.setState({ isAlertOpen: true, errorMessage: msg });
   }
   closeAlert() {
      this.setState({ isAlertOpen: false, errorMessage: '' });
   }

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   openUserDialog() {
      this.setState({
         isUserDialogOpen: true
      });
   }
   closeUserDialog() {
      this.setState({
         isUserDialogOpen: false
      });
   }

   openUserEditDialog() {
      this.setState({
         isUserEditDialogOpen: true
      });
   }
   closeUserEditDialog() {
      this.setState({
         isUserEditDialogOpen: false
      });
   }

   openDeleteAlert() {
      if (this.state.selected === undefined) {
         return;
      }
      this.setState({
         isDeleteAlertOpen: true
      });
   }
   closeDeleteAlert() {
      this.setState({
         isDeleteAlertOpen: false
      });
   }

   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }

   handleTabChange = activeTabID => {
      this.setState({
         activeTabID: activeTabID,
         selectedTabId: activeTabID
      });
   };
   clearForm() {
      this.setState({
         email: '',
         username: '',
         usertype: 'standard',
         password: '',
         isAlertOpen: false,
         errorMessage: ''
      });
   }

   renderControlls() {
      if (this.state.selectedTabId === 'Users') {
         return (
            <ButtonGroup>
               <Button
                  icon='edit'
                  small
                  minimal
                  onClick={() => this.openUserEditDialog()}
               />
               <Button
                  icon='add'
                  small
                  minimal
                  onClick={() => this.openUserDialog()}
               />
               <Button
                  icon='trash'
                  small
                  minimal
                  onClick={() => this.openDeleteAlert()}
               />
               <Divider />
               <Button
                  icon='refresh'
                  small
                  minimal
                  onClick={() => this.getUsersData()}
               />
            </ButtonGroup>
         );
      }
   }

   render() {
      const {
         users,
         isLoading,
         email,
         username,
         usertype,
         password,
         isAlertOpen,
         selected
      } = this.state;

      return (
         <div id='AdminPanel'>
            <div className='mt-2'>
               <H2>Admin Panel</H2>
            </div>
            <Tabs
               onChange={this.handleTabChange}
               selectedTabId={this.state.selectedTabId}
               large
            >
               <Tab
                  id='Users'
                  title='Users'
                  panel={
                     <UsersTable
                        usersData={users}
                        onRefresh={() => this.getUsersData()}
                        isLoading={isLoading}
                        onSelection={this.getSelected}
                     />
                  }
               />
               <Tabs.Expander />
               {this.renderControlls()}
            </Tabs>
            <UserDialog
               isOpen={this.state.isUserEditDialogOpen}
               toOpen={() => this.openUserEditDialog()}
               toClose={() => this.closeUserEditDialog()}
               email={selected.email}
               username={selected.username}
               password={selected.password}
               usertype={selected.usertype}
               onChange={this.onChange}
               handleSubmit={this.handleSubmit}
               buttonCaption='Add'
               onClear={() => this.clearForm()}
               isAlertOpen={isAlertOpen}
               closeAlert={() => this.closeAlert()}
            />
            <UserDialog
               isOpen={this.state.isUserDialogOpen}
               toOpen={() => this.openUserDialog()}
               toClose={() => this.closeUserDialog()}
               email={email}
               username={username}
               password={password}
               usertype={usertype}
               onChange={this.onChange}
               handleSubmit={this.handleSubmit}
               buttonCaption='Add'
               onClear={() => this.clearForm()}
               isAlertOpen={isAlertOpen}
               closeAlert={() => this.closeAlert()}
            />
            <Alert
               isOpen={this.state.isDeleteAlertOpen}
               confirmButtonText='Delete'
               onConfirm={() => this.deleteUser(selected._id)}
               cancelButtonText='Cancel'
               onCancel={() => this.closeDeleteAlert()}
               intent='danger'
               icon='issue'
               ussePortal={
                  <Portal container={document.getElementById('AdminPanel')} />
               }
            >
               <H5>Are you sure that you want to delete this user?</H5>
               <strong>{`User: ${selected.username}`}</strong>
               <p></p>
               <strong
                  className={Classes.TEXT_MUTED}
               >{`Will be permanentyly deleted...`}</strong>
            </Alert>
         </div>
      );
   }
}
