import React, { Component } from "react";
import {
   Row,
   Col,
   Card,
   CardBody,
   Button,
   Form,
   FormGroup,
   Input,
   InputGroup,
   InputGroupAddon,
   InputGroupText,
   Alert
} from "reactstrap";
import Auth from "../../Modules/Auth/Auth";
import axios from "axios";
import MainContainer from "../../components/MainContainer/MainContainer";

import "./Login.css";
import "bootstrap/dist/css/bootstrap.css";
import "@fortawesome/fontawesome-free/css/all.css";

export default class Login extends Component {
   state = {
      theme: "dark",
      buttonColor: "secondary",
      alertVisible: true,
      isLoading: false,
      authenticated: false,
      message: "",
      email: "",
      password: ""
   };

   onSubmit = e => {
      e.preventDefault();

      const { email, password } = this.state;

      const data = {
         user: {
            email: email,
            password: password
         }
      };

      const url = "http://localhost:3001/api/users/login";

      axios
         .post(url, data)
         .then(result => {
            //localStorage.setItem("jwtToken", result.data.token);
            Auth.authenticateUser("token", result.data.token);
            this.setState({ message: "ok" });
            this.props.history.push("/Home");
         })
         .catch(error => {
            this.setState({
               message: "Login failed. Username or password not match"
            });
            console.log(error);
         });
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
      this.setLoginButtonColor();
   };

   setLoginButtonColor() {
      const { email, password } = this.state;
      if (email && password) {
         this.setState({
            buttonColor: "primary"
         });
      }
   }

   render() {
      const { email, password, message } = this.state;

      return (
         <MainContainer theme="" displayBgAnimation={true}>
            <Row>
               <Col
                  xl={{ size: 4, offset: 4 }}
                  lg={{ size: 4, offset: 4 }}
                  md={{ size: 4, offset: 4 }}
                  sm="12"
                  xs="12"
                  className="mt-4"
               >
                  <Card inverse color="dark" className="bg-login-form mt-5">
                     <CardBody className="bg-login-form">
                        <Form>
                           <span className="fas fa-dice-d20 float-right" />
                           <p className="text-center">Sign in</p>
                           {message !== "" && (
                              <Alert className="bg-dark border border-warning text-warning rounded-corners text-center">
                                 {message}
                              </Alert>
                           )}
                           <FormGroup>
                              <InputGroup size="sm">
                                 <InputGroupAddon addonType="prepend">
                                    <InputGroupText>
                                       <i className="fas fa-user" />
                                    </InputGroupText>
                                 </InputGroupAddon>
                                 <Input
                                    type="email"
                                    name="email"
                                    id="email"
                                    placeholder="email"
                                    size="sm"
                                    value={email}
                                    onChange={this.onChange}
                                 />
                              </InputGroup>
                           </FormGroup>
                           <FormGroup>
                              <InputGroup size="sm">
                                 <InputGroupAddon addonType="prepend">
                                    <InputGroupText>
                                       <i className="fas fa-key" />
                                    </InputGroupText>
                                 </InputGroupAddon>
                                 <Input
                                    color={this.state.buttonColor}
                                    type="password"
                                    name="password"
                                    id="password"
                                    placeholder="password"
                                    size="sm"
                                    value={password}
                                    onChange={this.onChange}
                                 />
                              </InputGroup>
                           </FormGroup>
                           <Button
                              type="submit"
                              className="float-right"
                              size="sm"
                              color={this.state.buttonColor}
                              onClick={this.onSubmit}
                           >
                              Login
                           </Button>
                        </Form>
                     </CardBody>
                  </Card>
               </Col>
            </Row>
         </MainContainer>
      );
   }
}
