import React, { Component } from 'react';
import { HTMLTable, Button, NonIdealState, H5 } from '@blueprintjs/core';
import { Table, Column } from '@blueprintjs/table';

import Client from '../../../../Modules/Client/Client';
import UsersTableRows from './UsersTableRows';
import UsersTableHeader from './UsersTableHeader';

export default class UsersTable extends Component {
   constructor(props) {
      super(props);
      this.state = {
         users: [],
         isLoading: false,
         errorMsg: 'asd'
      };
   }

   componentDidMount() {
      this.getData();
   }

   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }

   async getData() {
      this.setLoading(true);
      var result;
      try {
         result = await Client.Services.UsersService.getUsers();
         this.setState({
            users: result.data
         });
         console.log(this.state.users);
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error while loading users list');
      } finally {
         this.setLoading(false);
      }
   }

   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th>User Name</th>
               <th>Role</th>
               <th>Role</th>
               <th className='text-center'>Action</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      return this.state.users.map(user => {
         return (
            <tr key={user._id}>
               <td>{user.username}</td>
               <td>{user.email}</td>
               <td>{user.role}</td>
               <td className='text-center'>
                  <Button minimal icon='more'></Button>
               </td>
            </tr>
         );
      });
   }

   renderContent() {
      if (this.state.errorMsg) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>Something went wrong</strong>
               </H5>
               <i>Cannot load users table</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => this.getData()}
               ></Button>
            </NonIdealState>
         );
      }
      return (
         <HTMLTable fill condensed striped>
            {this.renderTableHeader()}
            {this.renderTableBody()}
         </HTMLTable>
      );
   }

   render() {
      return <div>{this.renderContent()}</div>;
   }
}
