import React, { Component } from 'react'
import { Collapse, Col, Card, CardHeader, CardBody } from 'reactstrap';
import Chart from '../../Charts/Chart/Chart'

import 'bootstrap/dist/css/bootstrap.min.css'
import '@fortawesome/fontawesome-free/css/all.min.css'

export default class CardWithChart extends Component {

   state = {
      theme: this.props.theme,
      collapse: true
   }

   toggle = () => {
      this.setState(state => ({ collapse: !state.collapse }));
   }

   setTheme(theme){
      if(theme === 'dark'){
         return 'bg-dark text-white'
      }
      if(theme === 'light'){
         return 'bg-light'
      }
   }

   render() {

      const { headerText, chartType, data, height, width } = this.props;

      return (
         <Col xl="6" lg="6" md="6" sm="12" xs="12" className="mt-4">
            <Card className={this.setTheme(this.state.theme)}>
               <CardHeader>
                  <span class="fas fa-chart-line header-icon"></span>
                  {headerText}
                  <span class="fa fa-angle-down float-right header-icon text-secondary" onClick={this.toggle}></span>
                  <span className="fas fa-toggle-on float-right header-icon text-secondary"></span>
               </CardHeader>
               <Collapse isOpen={this.state.collapse}>
                  <CardBody className="card-body">
                     <Chart 
                        theme={this.state.theme} 
                        chartType={chartType} 
                        data={data} width={width} 
                        height={height}/>
                  </CardBody>
               </Collapse>
            </Card>
         </Col >
      )
   }
}
