import React, { Component } from "react";
import axios from "axios";
import StdCard from "../Card/StdCard";
import Select from "../Buttons/Select/Select";
import {
   Row,
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   Label,
   Input
} from "reactstrap";

export default class CreateUser extends Component {
   state = {
      theme: "dark",
      userType: "",
      userTypes: [
         {
            name: "Standard",
            value: "Standard"
         },
         {
            name: "Admin",
            value: "Admin"
         },
         {
            name: "Root",
            value: "Root"
         }
      ],
      userName: "",
      email: "",
      project: "",
      projects: [
         {
            name: "GDC",
            value: "GDC"
         }
      ],
      created: false,
      message: "asd",
      isLoading: false
   };

   createUser() {
      const { userName, userType, email, project } = this.state;

      this.setState({ isLoading: true });

      const URL = "/api/users/create";

      const userData = {
         username: userName,
         email: email,
         userType: userType,
         project: project
      };

      axios
         .post(URL, userData)
         .then(res => {
            this.setState({ created: res.data.created });
         })
         .catch(err => {
            this.setState({ created: "error", message: this.data.message });
            console.log(err);
         });

      this.setState({ isLoading: false });
   }

   setInputTheme(theme) {
      if (theme === "dark") {
         return "table-input-dark";
      }
      if (theme === "light") {
         return "";
      }
   }

   render() {
      const { message } = this.state;

      return (
         <StdCard
            headerText="Create new user"
            icon="fas fa-user"
            theme={this.state.theme}
         >
            <Form>
               <Row>
                  <Col md={12}>
                     {message !== "" && (
                        <Alert className="bg-transparent border border-warning text-warning rounded-corners text-center">
                           {message}
                        </Alert>
                     )}
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="project">User Type</Label>
                        <Select
                           options={this.state.userTypes}
                           theme={this.state.theme}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="project">Project</Label>
                        <Select
                           options={this.state.projects}
                           theme={this.state.theme}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="email">Username</Label>
                        <Input
                           type="text"
                           name="username"
                           id="username"
                           size="sm"
                           placeholder="Please provide username"
                           className={this.setInputTheme(this.state.theme)}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="email">Email</Label>
                        <Input
                           type="email"
                           name="email"
                           id="exampleEmail"
                           size="sm"
                           placeholder="Please provide email"
                           className={this.setInputTheme(this.state.theme)}
                        />
                     </FormGroup>
                  </Col>
               </Row>
               <div className="text-right">
                  <Button color="primary" onClick={this.createUser}>
                     Create
                  </Button>
               </div>
            </Form>
         </StdCard>
      );
   }
}
