import React, { Component } from 'react';
import {
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   FormText,
   Label,
   Input,
   ButtonGroup
} from 'reactstrap';
import axios from 'axios';

export default class CreateEmployeeForm extends Component {
   state = {
      theme: this.props.theme,
      sap: '',
      name: '',
      email: '',
      role: '',
      supportType: '',
      project: '',
      mainlanguage: '',
      rm: '',
      message: ''
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   onSubmit = e => {
      e.preventDefault();

      const {
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm
      } = this.state;

      const URL = 'http://localhost:3001/api/employees/';

      const data = {
         employee: {
            sap: sap,
            name: name,
            email: email,
            role: role,
            supportType: supportType,
            project: project,
            mainlanguage: mainlanguage,
            rm: rm
         }
      };

      axios
         .post(URL, data)
         .then(res => {
            this.setState({
               created: 'ok',
               message: 'Employee saved successfully'
            });
            document.getElementById('getEmployeesData').click();
         })
         .catch(err => {
            this.setState({
               created: 'error',
               message: 'Something went wrong',
               err: err
            });
         });
   };

   clearForm = () => {
      this.setState({
         sap: '',
         name: '',
         email: '',
         role: '',
         supportType: '',
         project: '',
         mainlanguage: '',
         rm: '',
         message: ''
      });
   };

   setInputTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   alertStyle() {
      const { created } = this.state;
      if (created === 'ok') return 'border-success text-success';
      if (created === 'error') return 'border-danger text-danger';
   }

   render() {
      const {
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm,
         message
      } = this.state;

      return (
         <div>
            <h5>Create New Employee</h5>
            {message !== '' && (
               <Alert
                  className={`bg-transparent border rounded-corners text-center ${this.alertStyle()}`}
               >
                  {message}
               </Alert>
            )}
            <Form className='mb-2' ref='createProjectForm'>
               <FormGroup row>
                  <Label className='text-right' for='sap' md={3} size='sm'>
                     SAP
                  </Label>
                  <Col md={9}>
                     <Input
                        type='number'
                        name='sap'
                        id='sap'
                        ref='sap'
                        size='sm'
                        value={sap}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide SAP Number</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='sap' md={3} size='sm'>
                     Name
                  </Label>
                  <Col md={9}>
                     <Input
                        type='text'
                        name='name'
                        id='name'
                        ref='name'
                        size='sm'
                        value={name}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide Employee Name</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='email' md={3} size='sm'>
                     Email
                  </Label>
                  <Col md={9}>
                     <Input
                        type='email'
                        name='email'
                        id='email'
                        ref='email'
                        size='sm'
                        value={email}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide email Number</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='role' md={3} size='sm'>
                     Role
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='role'
                        id='role'
                        ref='role'
                        size='sm'
                        value={role}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide Role</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='email' md={3} size='sm'>
                     Support Type
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='supportType'
                        id='supportType'
                        ref='supportType'
                        size='sm'
                        value={supportType}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide support type</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='Project' md={3} size='sm'>
                     Project
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='project'
                        id='project'
                        ref='project'
                        size='sm'
                        value={project}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide Project Name</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label
                     className='text-right'
                     for='mainlanguage'
                     md={3}
                     size='sm'
                  >
                     Main Language
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='mainlanguage'
                        id='mainlanguage'
                        ref='mainlanguage'
                        size='sm'
                        value={mainlanguage}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide main language</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='rm' md={3} size='sm'>
                     Reporting Manager
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='rm'
                        id='rm'
                        ref='rm'
                        size='sm'
                        value={rm}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide Reporting Manager Name</FormText>
                  </Col>
               </FormGroup>

               <div className='text-right'>
                  <ButtonGroup>
                     <Button size='sm' onClick={this.clearForm}>
                        Clear
                     </Button>
                     <Button color='primary' size='sm' onClick={this.onSubmit}>
                        Add
                     </Button>
                  </ButtonGroup>
               </div>
            </Form>
         </div>
      );
   }
}
