import React, { Component } from 'react';
import {
   Navbar,
   Button,
   Alignment,
   Menu,
   MenuItem,
   MenuDivider,
   Popover,
   Position,
   Icon
} from '@blueprintjs/core';
import UIManager from '../../Modules/UIManager/UIManager';

export default class AppBar extends Component {
   constructor(props) {
      super(props);
      this.state = {
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark'
      };
      this.switchTheme = this.switchTheme.bind(this);
   }

   switchTheme() {
      UIManager.swtichTheme();
      this.setState({
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark'
      });
   }

   render() {
      var exampleMenu = (
         <Menu>
            <MenuItem icon='user' text='Profile' />
            <MenuItem icon='cog' text='Settings...'></MenuItem>
            <MenuItem
               icon={this.state.themeIcon}
               text={this.state.themeToSwitch + ' mode'}
               onClick={this.switchTheme}
            />
            <MenuDivider />
            <MenuItem icon='log-out' text='Logout...'></MenuItem>
         </Menu>
      );
      var mainMenu = (
         <Menu>
            <MenuItem icon='application' text='About this application' />
            <MenuItem
               icon='applications'
               text='Apps'
               active={false}
               shouldDismissPopover={true}
            >
               <MenuItem icon='cog' text='Settings...'></MenuItem>
            </MenuItem>
            <MenuItem icon='cog' text='Settings...'></MenuItem>
            <MenuDivider />
            <MenuItem icon='log-out' text='Logout...'></MenuItem>
         </Menu>
      );

      return (
         <Navbar fixedToTop>
            <Navbar.Group align={Alignment.LEFT}>
               <Popover
                  content={mainMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Icon icon='ring'></Icon>
               </Popover>

               <Navbar.Divider />
               <Button minimal icon='home' text='' small />
            </Navbar.Group>
            <Navbar.Group align={Alignment.RIGHT}>
               <Popover
                  content={exampleMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button className='bp3-minimal' icon='properties' text='' />
               </Popover>
            </Navbar.Group>
         </Navbar>
      );
   }
}
