import React, { Component } from 'react'
import '@fortawesome/fontawesome-free/css/all.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import './Quic.css'
import ScoreCard from './ScoreCard/ScoreCard';

export default class Quick extends Component {

   state = {
      headcount: 0,
      headcountProc: 0,
      eHeadcount: 0,
      eHeadcountProc: 0,
      productivity: 0,
      productivityProc: 0,
      eProductivity: 0,
      eProductivityProc: 0,
      availability: 0,
      availabilityProc: 0,
      shrinkage: 0,
      shrinkageProc: 0
   }

   componentDidMount() {
      this.getHeadcount();
   }

   getHeadcount() {
      fetch('/api/users/prodsummary')
         .then(res => res.json())
         .then(({ headcount, proc }) => this.setState({ headcount, proc }))
   }

   render() {
      return (
         <div className="row">
            <ScoreCard icon="fa fa-users" title="Headcount" procValue={this.state.proc} numberValue={this.state.headcount} trend="0"></ScoreCard>
            <ScoreCard icon="fa fa-user" title="E.Headcount" procValue={this.state.proc} numberValue={this.state.headcount} trend="-1"></ScoreCard>
            <ScoreCard icon="fa fa-chart-line" title="Productivity" procValue={this.state.proc} numberValue={this.state.headcount} trend="1"></ScoreCard>
            <ScoreCard icon="fa fa-chart-line" title="E. Productivity" procValue={this.state.proc} numberValue={this.state.headcount} trend="1"></ScoreCard>
            <ScoreCard icon="fa fa-clock" title="Availability" procValue={this.state.proc} numberValue={this.state.headcount} trend="-1"></ScoreCard>
            <ScoreCard icon="fa fa-stopwatch" title="Shrinkage" procValue={this.state.proc} numberValue={this.state.headcount} trend="0"></ScoreCard>
         </div>
      )
   }
}
