import React, { Component } from 'react';
import { Button, FormGroup, InputGroup, ButtonGroup } from '@blueprintjs/core';
import UserRolesOptions from '../../../../components/Selects/UserRolesOptions';

export default class UserForm extends Component {
   render() {
      return (
         <div>
            <FormGroup
               label='Username'
               labelFor='username'
               labelInfo='(required)'
            >
               <InputGroup
                  id='username'
                  leftIcon='user'
                  onChange={this.props.onChange}
                  placeholder='Username...'
                  type='email'
                  value={this.props.username}
                  fill
               />
            </FormGroup>
            <FormGroup label='Email' labelFor='email' labelInfo='(required)'>
               <InputGroup
                  id='email'
                  leftIcon='envelope'
                  onChange={this.props.onChange}
                  placeholder='Email...'
                  type='email'
                  value={this.props.email}
                  fill
               />
            </FormGroup>
            <FormGroup
               label='Password'
               labelFor='password'
               labelInfo='(required)'
            >
               <InputGroup
                  id='password'
                  leftIcon='key'
                  onChange={this.props.onChange}
                  placeholder='Username...'
                  type='password'
                  value={this.props.password}
                  fill
               />
            </FormGroup>
            <FormGroup label='Role' labelFor='role' labelInfo='(required)'>
               <UserRolesOptions
                  id='role'
                  onChange={this.props.onChange}
                  value={this.props.role}
                  fill
               />
            </FormGroup>
            <div className='text-right'>
               <ButtonGroup>
                  <Button onClick={this.props.onClear}>
                     {this.props.clearButtonCaption}
                  </Button>
                  <Button onClick={this.props.onClick}>
                     {this.props.buttonCaption}
                  </Button>
               </ButtonGroup>
            </div>
         </div>
      );
   }
}
UserForm.defaultProps = {
   clearButtonCaption: 'Clear'
};
