import React, { Component } from 'react';
import ProjectTable from '../../../Table/ProjectTable/ProjectTable';

export default class EditProjectForm extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: this.props.theme,
         projectName: '',
         projects: []
      };
   }

   setSelectTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const { theme, projectName } = this.state;

      return (
         <div>
            <ProjectTable theme={theme} name={projectName} edit remove retire />
         </div>
      );
   }
}
