import React, { Component } from 'react';
import { Form } from 'reactstrap';
import {
   Button,
   ButtonGroup,
   FormGroup,
   InputGroup,
   HTMLTable
} from '@blueprintjs/core';

export default class EmployeeLanguageForm extends Component {
   render() {
      const {
         onChange,
         onSubmit,
         buttonCaption,
         onClear,
         language
      } = this.props;
      return (
         <Form className='mb-2' ref='createLanguageForm'>
            <HTMLTable condensed>
               <tr>
                  <td>
                     <FormGroup
                        label='language'
                        labelFor='language'
                        labelInfo='(required)'
                     ></FormGroup>
                  </td>
                  <td colSpan='3'>
                     <InputGroup
                        id='language'
                        onChange={onChange}
                        placeholder='Language...'
                        type='number'
                        value={language}
                        fill
                     />
                  </td>
               </tr>
            </HTMLTable>

            <div className='text-right'>
               <ButtonGroup>
                  <Button icon='refresh' onClick={onClear}>
                     Clear
                  </Button>
                  <Button icon='saved' onClick={onSubmit}>
                     {buttonCaption}
                  </Button>
               </ButtonGroup>
            </div>
         </Form>
      );
   }
}
