import React, { Component } from 'react';
import { Tabs, Tab, H2, Spinner, NonIdealState } from '@blueprintjs/core';
import Client from '../../Modules/Client/Client';
import MainDashboard from './components/MainDashboard';
import DashboardControllers from './components/DashboardControllers';
import TestView2 from './components/TestComponent2';
import ProjectOptions from '../../components/Selects/ProjectOptions';

export default class Dashboard extends Component {
   state = {
      activeTabID: 'Dashboards',
      selectedTabId: 'Dashboards',
      isLoading: true,
      data: [],
      errorMessage: '',
      project: '',
      target: 100,
      labels: [1, 2, 3, 4, 5, 6, 6, 78, 8, 8, 90],
      series: [
         {
            name: 'series-1',
            data: [30, 40, 45, 50, 49, 60, 70, 91]
         }
      ]
   };

   componentDidMount() {
      this.getData();
   }

   async getData() {
      this.setLoading(true);
      const { project } = this.state;

      if (project === '') {
         this.setLoading(false);
         return;
      }

      try {
         //this.resetErrorMsg();
         var result = await Client.Services.ReportingService.getReportByProjectID(
            project
         );
         this.setState({
            data: result.data
         });
         console.log(result.data);
      } catch (err) {
         alert(err);
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading reports');
      } finally {
         this.setLoading(false);
      }
   }

   resetErrorMsg() {
      this.setState({ errorMessage: '' });
   }

   onChangeProject = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
      this.getData();
   };

   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }

   handleTabChange = activeTabID => {
      this.setState({
         activeTabID: activeTabID,
         selectedTabId: activeTabID
      });
   };

   renderControlls() {
      if (this.state.selectedTabId === 'Dashboards') {
         return <DashboardControllers onRefresh={() => this.getData()} />;
      }
   }
   renderContent() {
      const { project, isLoading, data } = this.state;
      if (project === '') {
         return <p>no project seleted yet</p>;
      }

      if (isLoading) {
         return <Spinner></Spinner>;
      }

      if (data.length === 0) {
         return (
            <NonIdealState
               icon='issue'
               title="Selected Project don't have KPIs"
               description='Please select different Project'
            >
               <ProjectOptions
                  id='project'
                  ref='project'
                  onChange={this.onChangeProject}
                  value={this.state.project}
                  placeholder='Select Project...'
               />
            </NonIdealState>
         );
      }

      return (
         <Tabs
            onChange={this.handleTabChange}
            selectedTabId={this.state.selectedTabId}
            large
         >
            <Tab
               id='Dashboards'
               title='Main Dashboard'
               panel={<MainDashboard />}
            />
            <Tab
               id='2'
               title='Beta'
               panel={
                  <TestView2
                     labels={this.state.labels}
                     yaxisLabel='Results'
                     xasisLabel='Reports'
                     series={this.state.series}
                     targetValue={this.state.target}
                     targetInfo='KPI target'
                  />
               }
            />
            <Tabs.Expander />
            {this.renderControlls()}
         </Tabs>
      );
   }

   render() {
      return (
         <div id='Dashboards'>
            <div className='mt-2'>
               <H2>
                  Dashboard Panel
                  <div className='float-right'>
                     <ProjectOptions
                        id='project'
                        ref='project'
                        onChange={this.onChangeProject}
                        value={this.state.project}
                        placeholder='Select Project...'
                     />
                  </div>
               </H2>
            </div>
            {this.renderContent()}
         </div>
      );
   }
}
