import React, { Component } from 'react';

import UsersCard from './components/Users/UsersCard';

import { Tabs, Tab, Button, H2, ButtonGroup, Divider } from '@blueprintjs/core';
import UsersTable from './components/Users/UsersTable';
import Client from '../../Modules/Client/Client';
import UserDialog from './components/Users/UserDialog';

export default class AdminPanel extends Component {
   state = {
      activeTabID: 'Users',
      selectedTabId: 'Users',
      isLoading: true,
      users: [],
      openAlert: true,
      errorMessage: '',
      isUserDialogOpen: false,
      username: '',
      email: '',
      password: '',
      usertype: '',
      selected: ''
   };

   getSelected = sel => {
      this.setState({
         selected: sel
      });
   };

   componentDidMount() {
      this.getUsersData();
   }

   async getUsersData() {
      this.setLoading(true);
      var result;
      try {
         //this.resetErrorMsg();
         result = await Client.Services.UsersService.getUsers();
         this.setState({
            users: result.data
         });
      } catch (err) {
         alert(err);
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading users list');
      } finally {
         this.setLoading(false);
      }
   }

   async deleteUser(id) {
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.UsersService.deleteUser(id);
         if (result.status === 200) {
            Client.Notifications.success('User removed successfully');
         }
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error, cannot delete user');
      } finally {
         this.props.onRefresh();
      }
   }

   handleSubmit = async e => {
      e.preventDefault();
      const { email, username, usertype, password } = this.state;
      var errorMsg = 'Unknown Error';
      let usr = Client.Services.UsersService.createUserDTO(
         username,
         email,
         password,
         usertype
      );
      try {
         let result = await Client.Services.UsersService.saveNewUser(usr);
         if (result.status === 200) {
            Client.Notifications.success(`User ${username} successfully saved`);
            this.getUsersData();
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${username} user`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };
   openAlert(msg) {
      this.setState({ isAlertOpen: true, errorMessage: msg });
   }
   closeAlert() {
      this.setState({ isAlertOpen: false, errorMessage: '' });
   }

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   openUserDialog() {
      this.setState({
         isUserDialogOpen: true
      });
   }
   closeUserDialog() {
      this.setState({
         isUserDialogOpen: false
      });
   }

   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }

   handleTabChange = activeTabID => {
      this.setState({
         activeTabID: activeTabID,
         selectedTabId: activeTabID
      });
   };
   clearForm() {
      this.setState({
         email: '',
         username: '',
         usertype: 'standard',
         password: '',
         isAlertOpen: false,
         errorMessage: ''
      });
   }

   renderControlls() {
      if (this.state.selectedTabId === 'Employees') {
         return (
            <ButtonGroup>
               <Button
                  icon='edit'
                  small
                  minimal
                  onClick={() => alert(this.state.selected)}
               />
               <Button
                  icon='add'
                  small
                  minimal
                  onClick={() => this.openUserDialog()}
               />
               <Button icon='trash' small minimal />
               <Divider />
               <Button
                  icon='refresh'
                  small
                  minimal
                  onClick={() => this.getUsersData()}
               />
            </ButtonGroup>
         );
      }
   }

   render() {
      const { users, isLoading } = this.state;
      const { email, username, usertype, password, isAlertOpen } = this.state;

      return (
         <div>
            <div className='mt-2'>
               <H2>Admin Panel</H2>
            </div>
            <Tabs
               onChange={this.handleTabChange}
               selectedTabId={this.state.selectedTabId}
               large
            >
               <Tab id='Users' title='Users' panel={<UsersCard />} />
               <Tab
                  id='Employees'
                  title='Employees'
                  panel={
                     <UsersTable
                        usersData={users}
                        onRefresh={() => this.getUsersData()}
                        isLoading={isLoading}
                        parentCallback={this.callbackFunction}
                     />
                  }
               />
               <Tabs.Expander />
               {this.renderControlls()}
            </Tabs>
            <UserDialog
               isOpen={this.state.isUserDialogOpen}
               toOpen={() => this.openUserDialog()}
               toClose={() => this.closeUserDialog()}
               email={email}
               username={username}
               password={password}
               usertype={usertype}
               onChange={this.onChange}
               handleSubmit={this.handleSubmit}
               buttonCaption='asd'
               onClear={() => this.clearForm()}
               isAlertOpen={isAlertOpen}
               closeAlert={() => this.closeAlert()}
            ></UserDialog>
         </div>
      );
   }
}
