import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import '../../QuickView/QuickView.css'

import { Card, CardTitle, CardBody, Badge } from 'reactstrap';
import { Col } from 'reactstrap';

export default class ScoreCard extends Component {

   state = {
      name: 'N/A',
      icon: '',
      proc: 0,
      number: 0,
      bgColor: 'bg-c-white',
      txtColor: 'text-gray'
   }

   getTrend = (value) => {
      if (value < 0) return "-";
      else if (value > 0) return "+";
      else return " ";
   }

   getBgColor = (trend) => {
      this.state.txtColor = "text-white";
      if (trend < 0) return "bg-danger";
      else if (trend > 0) return "bg-success ";
      else {
         this.state.txtColor = "text-black";
         return '';
      }
   }

   getIcon = (iconName) => {
      if (!iconName) return 'fas fa-bug';
      else return iconName;
   }

   render() {

      const { title, icon, procValue, numberValue, trend } = this.props;

      return (
         <Col xl="2" lg="3" md="4" sm="6" xs="12" className="mt-4">
            <Card inverse color="dark">
               <CardBody>
                  <CardTitle>
                     <b>{title}</b>
                  </CardTitle>
                  <Badge className={`${this.getBgColor(trend)} ${this.state.txtColor} float-right`}>
                        {this.getTrend(this.props.trend) + procValue}%
                     </Badge>
                  <h1>
                     <span className={this.getIcon(icon)}></span>
                     <span className="float-right text-big">{numberValue}</span>
                  </h1>
               </CardBody>
            </Card>
         </Col>
      )
   }
}
