import React, { Component } from "react";
import { Col, Row, Button, Form, FormGroup, Label, Input } from "reactstrap";
import TopBar from "../Nav/TopBar/TopBar";
import LeftBar from "../Nav/LeftBar/LeftBar";
import NavItem from "../Nav/NavItem/NavItem";
import AppSettings from "./AppSettings/AppSettings";
import UserSettings from "./UserSettings/UserSettings";
import StdCard from "../Card/StdCard";
import Select from "../Buttons/Select/Select";
import MainContainer from "../MainContainer/MainContainer";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

export default class Settings extends Component {
   state = {
      theme: "dark",
      options: [
         {
            name: "a",
            value: "a"
         }
      ]
   };

   setTheme(theme) {
      if (theme === "dark") {
         return "table-dark";
      }
      if (theme === "light") {
         return "";
      }
   }
   setInputTheme(theme) {
      if (theme === "dark") {
         return "table-input-dark";
      }
      if (theme === "light") {
         return "";
      }
   }

   render() {
      return (
         <Router>
            <MainContainer theme={this.state.theme}>
               <TopBar theme={this.state.theme} />
               <LeftBar theme={this.state.theme}>
                  <Link to="/Settings/User">
                     <NavItem icon="fas fa-user-cog" />
                  </Link>
                  <Link to={`/Settings/App`}>
                     <NavItem icon="fas fa-sliders-h" />
                  </Link>
               </LeftBar>
               <Row>
                  <StdCard
                     theme={this.state.theme}
                     icon="fas fa-user"
                     headerText="User Settings"
                  >
                     <Form>
                        <Row form>
                           <Col md={12}>
                              <FormGroup>
                                 <Label for="username">Username</Label>
                                 <Input
                                    type="text"
                                    name="Username"
                                    id="username"
                                    placeholder="Username"
                                    className={this.setInputTheme(
                                       this.state.theme
                                    )}
                                    disable
                                 />
                              </FormGroup>
                           </Col>
                           <Col md={6}>
                              <FormGroup>
                                 <Label for="exampleEmail">Email</Label>
                                 <Input
                                    type="email"
                                    name="email"
                                    id="exampleEmail"
                                    placeholder="with a placeholder"
                                    className={this.setInputTheme(
                                       this.state.theme
                                    )}
                                 />
                              </FormGroup>
                           </Col>
                           <Col md={6}>
                              <FormGroup>
                                 <Label for="examplePassword">Password</Label>
                                 <Input
                                    type="password"
                                    name="password"
                                    id="examplePassword"
                                    placeholder="password placeholder"
                                    className={this.setInputTheme(
                                       this.state.theme
                                    )}
                                 />
                              </FormGroup>
                           </Col>
                        </Row>
                        <FormGroup>
                           <Label for="project">Project</Label>
                           <Select
                              options={this.state.options}
                              theme={this.state.theme}
                           />
                        </FormGroup>
                        <Button>Sign in</Button>
                     </Form>
                  </StdCard>
                  <StdCard
                     icon="fas fa-align-left"
                     headerText="Application settings"
                     theme={this.state.theme}
                  />
               </Row>
            </MainContainer>
            <Route path={`/Settings/App`} component={AppSettings} />
            <Route path={`/Settings/App`} component={UserSettings} />
         </Router>
      );
   }
}
