import React, { Component } from 'react';
import { TabContent, TabPane } from 'reactstrap';
import Sidebar from '../../components/Sidebar/Sidebar';
import SidebarItem from '../../components/Sidebar/SidebarItem';
import DataManagerCard from '../../components/Card/DataManagerCard';
import UsersCard from './components/Users/UsersCard';
import Targets from './components/Targets/Targets';
import Employees from './components/Employees/Employees';
import Projects from './components/Projects/Projects';

export default class AdminPanel extends Component {
   state = {
      selected: 'users'
   };

   changeSelection(id) {
      this.setState({ selected: id });
   }

   render() {
      const { selected } = this.state;
      return (
         <div>
            <Sidebar>
               <SidebarItem
                  icon='user'
                  selected={selected === 'users'}
                  onClick={() => this.changeSelection('users')}
               />
               <SidebarItem
                  icon='people'
                  selected={selected === 'employees'}
                  onClick={() => this.changeSelection('employees')}
               />
               <SidebarItem
                  icon='gantt-chart'
                  selected={selected === 'projects'}
                  onClick={() => this.changeSelection('projects')}
               />
               <SidebarItem
                  icon='locate'
                  selected={selected === 'targets'}
                  onClick={() => this.changeSelection('targets')}
               />
            </Sidebar>

            <TabContent activeTab={selected}>
               <TabPane tabId='users'>
                  <UsersCard />
               </TabPane>
               <TabPane tabId='employees'>
                  Employees
                  <Employees />
               </TabPane>
               <TabPane tabId='projects'>
                  projects
                  <Projects />
               </TabPane>
               <TabPane tabId='targets'>
                  targets
                  <Targets />
               </TabPane>
            </TabContent>
         </div>
      );
   }
}
