import React, { Component } from 'react';
import { CardBody } from 'reactstrap';
import { Card, Icon, Button, ButtonGroup, Dialog } from '@blueprintjs/core';
import StdCard from './StdCard';
import CardHeader from './CardHeader';
import Client from '../../Modules/Client/Client';

const ADMIN = 'ADMIN';
const ROOT = 'ROOT';

export default class DataManagerCard extends Component {
   constructor(props) {
      super(props);
      this.toggle = this.toggle.bind(this);
      this.state = {
         modal: false,
         tableData: this.props.data,
         userRole: ''
      };
   }
   componentDidMount() {
      this.setState({ userRole: Client.User.getRole() });
   }
   toggle() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }
   render() {
      const { modal, userRole } = this.state;

      return (
         <StdCard
            icon={this.props.icon}
            headerText={this.props.headerText}
            size={this.props.cardSize}
         >
            <h5>
               {this.props.cardCaption}
               <ButtonGroup className='float-right'>
                  <Button
                     icon='add'
                     minimal
                     onClick={this.toggle}
                     disabled={userRole === ADMIN || userRole === ROOT}
                  >
                     {this.props.addButtonText}
                  </Button>
               </ButtonGroup>
            </h5>
            <hr />
            {this.props.table}
            {this.props.children}
            <Dialog
               id={this.props.id}
               isOpen={modal}
               onClose={this.toggle}
               toggle={this.toggle}
               //className='modal-full modal-my'
            >
               <Card>
                  <CardHeader>
                     <Icon icon='user' className='header-icon' />
                     {this.props.modalHeaderText}
                     <div className='float-right'>
                        <Icon icon='cross' onClick={this.toggle} />
                     </div>
                  </CardHeader>
                  <CardBody className='card-body'>
                     {this.props.addForm}
                  </CardBody>
               </Card>
            </Dialog>
         </StdCard>
      );
   }
}

DataManagerCard.defaultProps = {
   cardSize: 'xl',
   icon: 'square',
   headerText: 'headerText',
   cardCaption: 'cardCaption',
   addButtonText: 'addButtonText',
   id: 'id',
   modalHeaderText: 'modalHeaderText',
   addForm: '<div></div>',
   table: 'some data'
};
