import axios from 'axios'
import API from '../../API'
import Auth from '../../../Auth/Auth'

export default class AuthService {

   static getLoginItem(email, password) {
      const data = {
         user: {
            email: email,
            password: password
         }
      }
      return data
   }

   static async login(email, password) {
      return await axios(API.ROUTES.AUTH.LOGIN, this.getLoginItem(email, password))
         .then(result => {
            Auth.authenticateUser(result.data.user)
            return true
         }).catch(err => {
            console.log(err)
            return false
         })
   }

}
