import React, { Component } from 'react';
import {
   HTMLTable,
   Button,
   NonIdealState,
   H5,
   Spinner,
   Card,
   Switch
} from '@blueprintjs/core';

export default class KPIsTable extends Component {
   state = {
      errorMsg: '',
      showEditCard: false,
      kpiForEdit: {},
      selected: ''
   };

   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th className='text-center pl-3'>#</th>
               <th>KPI Name</th>
               <th>Valid From</th>
               <th>Valid Until</th>
               <th className='text-center'>Target</th>
               <th>Project</th>
               <th>Manager</th>
               <th>Info</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      const { data } = this.props;
      return data.map((kpi, key) => {
         return (
            <tr
               key={kpi._id}
               onClick={e => this.handleSelection(e, kpi)}
               className={`pl-2 ${
                  this.state.selected._id === kpi._id ? 'bg-selected' : ''
               }`}
            >
               <td className='text-center pl-3'>{key}</td>
               <td style={{ paddingLeft: '20px' }}>{kpi.name}</td>
               <td>{kpi.time_from}</td>
               <td>{kpi.time_to}</td>
               <td className='text-center'>{kpi.target}</td>
               <td>{kpi.project.name}</td>
               <td>{kpi.manager.name}</td>
            </tr>
         );
      });
   }

   handleSelection(e, kpi) {
      e.preventDefault();
      this.props.onSelection(kpi, false);
      this.setState({
         selected: kpi
      });
   }

   renderContent() {
      if (this.state.errorMsg) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>Something went wrong</strong>
               </H5>
               <i>Cannot load KPIs table</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => {
                     this.props.onRefresh();
                     this.setState({
                        errorMsg: ''
                     });
                  }}
               ></Button>
            </NonIdealState>
         );
      }
      return (
         <div className='overflow-x-scroll'>
            <HTMLTable fill='true' striped condensed interactive>
               {this.renderTableHeader()}
               {this.renderTableBody()}
            </HTMLTable>
         </div>
      );
   }
   render() {
      const { isLoading } = this.props;

      if (isLoading) {
         return <Spinner intent='primary'></Spinner>;
      }
      return <Card elevation='2'>{this.renderContent()}</Card>;
   }
}
KPIsTable.defaultProps = {
   data: [],
   onRefresh: function() {
      return;
   }
};
