import React, { Component } from 'react'
import {
   Card, CardHeader, CardFooter, CardBody,
   CardTitle, CardText
} from 'reactstrap';
import { Row, Col } from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css'

export default class Card_ extends Component {
   render() {

      const { headerText, bodyText, cardText } = this.props;

      return (
         <Col sm="3" xs="3" mt="4">
            <Card>
               <CardHeader>
                  {headerText}
               </CardHeader>
               <CardBody>
                  {bodyText}
                  <CardText>
                     {cardText}
                  </CardText>
               </CardBody>
            </Card>
         </Col>

         /*<div className="col-sm-2 col-xs-2 mt-4">
            <div className="card bg-dark text-white mt-4">
               <div className="card-header">
                  {headerText}
               </div>
               <div className="card-body">
                  {bodyText}
                  <p className="card-text">
                     {cardText}
                  </p>
               </div>
            </div>
         </div>*/
      )
   }
}
