import React, { Component } from 'react';
import UserForm from './UserForm';
import Client from '../../../Modules/Client/Client';
import { Alert, H5 } from '@blueprintjs/core';
import DataManagerCard from '../../../components/Card/DataManagerCard';
import UsersTable from './UsersTable';

export default class UsersCard extends Component {
   state = {
      email: '',
      username: '',
      role: 'USER',
      password: '',
      openAlert: false,
      errorMessage: '',
      errorMsg: ''
   };

   componentDidMount() {
      this.getUsersData();
   }

   async getUsersData() {
      this.setLoading(true);
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.UsersService.getUsers();
         this.setState({
            users: result.data
         });
      } catch (err) {
         console.log(err);
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading users list');
      } finally {
         this.setLoading(false);
      }
   }
   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }
   resetErrorMsg() {
      this.setState({
         errorMsg: ''
      });
   }
   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   closeAlert() {
      this.setState({ openAlert: false, errorMessage: '' });
   }

   clearForm() {
      this.setState({
         email: '',
         username: '',
         role: 'USER',
         password: '',
         openAlert: false,
         errorMessage: ''
      });
   }

   handleSubmit = async e => {
      e.preventDefault();

      const { email, username, role, password } = this.state;
      var errorMsg = 'Unknown Error';
      let usr = Client.Services.UsersService.createUserDTO(
         username,
         email,
         password,
         role,
         '5dfffe289fe19f21c85f3287', //<- temporaryfix
         '5dfffe289fe19f21c85f3287' //<- temporaryfix
      );
      try {
         let result = await Client.Services.UsersService.saveNewUser(usr);
         if (result.status === 200) {
            Client.Notifications.success(`User ${username} successfully saved`);
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${username} user`;
            Client.Notifications.error(errorMsg);
         }
         this.setState({
            errorMessage: errorMsg,
            openAlert: true
         });
      }
   };
   render() {
      const {
         email,
         username,
         role,
         password,
         users,
         errorMessage,
         openAlert
      } = this.state;
      return (
         <DataManagerCard
            icon='th'
            headerText='Users'
            cardCaption='All app users'
            addButtonText='Add new user'
            id='allUsers'
            modalHeaderText='Add new user'
            table={
               <UsersTable
                  usersData={users}
                  onRefresh={this.getUsersData()}
               ></UsersTable>
            }
            addForm={
               <UserForm
                  email={email}
                  username={username}
                  password={password}
                  role={role}
                  onChange={this.onChange}
                  onClick={this.handleSubmit}
                  buttonCaption='Create'
               />
            }
         >
            <Alert
               isOpen={openAlert}
               confirmButtonText='Clear form'
               onConfirm={() => this.clearForm()}
               cancelButtonText='Cancel'
               onCancel={() => this.closeAlert()}
               intent='danger'
               icon='issue'
               portalContainer={document.getElementById('allUsers')}
            >
               <H5>Error</H5>
               <H5>{errorMessage}</H5>
               <p>Do you want to clear the data?</p>
            </Alert>
         </DataManagerCard>
      );
   }
}
