import React, { Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import {
   Navbar,
   Button,
   Alignment,
   Menu,
   MenuItem,
   MenuDivider,
   Popover,
   Position,
   Breadcrumbs
} from '@blueprintjs/core';
import UIManager from '../../Modules/UIManager/UIManager';
import Client from '../../Modules/Client/Client';
import './AppBar.css';

const BREADCRUMBS = [{ href: '/home', icon: 'home' }];

export default class AppBar extends Component {
   constructor(props) {
      super(props);
      this.state = {
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark',
         logout: false,
         goHome: false
      };
      this.switchTheme = this.switchTheme.bind(this);
      this.handleLogout = this.handleLogout.bind(this);
   }

   switchTheme() {
      UIManager.swtichTheme();
      UIManager.setChartView();
      this.setState({
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark'
      });
   }

   handleLogout = e => {
      e.preventDefault();
      Client.logout();
      this.setState({
         logout: true
      });
   };

   render() {
      if (this.state.logout) {
         return <Redirect to='/login' />;
      }

      var rightMenu = (
         <Menu>
            <MenuItem icon='user' text='Profile' />
            <Link to='/Settings'>
               <MenuItem icon='cog' text='Settings...' />
            </Link>
            <MenuItem
               icon={this.state.themeIcon}
               text={this.state.themeToSwitch + ' mode'}
               onClick={this.switchTheme}
            />
            <MenuDivider />
            <MenuItem
               icon='log-out'
               text='Logout...'
               onClick={this.handleLogout}
            ></MenuItem>
         </Menu>
      );
      var mainMenu = (
         <Menu>
            <MenuItem icon='application' text='About this app...' />
            <MenuItem
               icon='applications'
               text='Apps'
               popoverProps={{ openOnTargetFocus: false }}
               shouldDismissPopover={true}
            >
               <Link to='/Dashboards'>
                  <MenuItem icon='dashboard' text='Dashboard' />
               </Link>
               <Link to='/Projects'>
                  <MenuItem icon='projects' text='Project' />
               </Link>
               <Link to='/Reports'>
                  <MenuItem icon='manually-entered-data' text='Reports' />
               </Link>
               <Link to='/AdminPanel'>
                  <MenuItem icon='control' text='Admin' />
               </Link>
            </MenuItem>
         </Menu>
      );

      return (
         <Navbar fixedToTop className='no-border border-bottom-0 pl-0'>
            <Navbar.Group align={Alignment.LEFT} className='adjust-nav-group '>
               <Popover
                  content={mainMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button minimal icon='circle' intent='primary'></Button>
               </Popover>
               <Navbar.Divider />
               <Breadcrumbs items={BREADCRUMBS} />
            </Navbar.Group>
            <Navbar.Group align={Alignment.RIGHT}>
               <Popover
                  content={rightMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button className='bp3-minimal' icon='property' text='' />
               </Popover>
            </Navbar.Group>
         </Navbar>
      );
   }
}
