import React, { Component } from 'react';
import SLATable from '../../Table/SLAsTable/SLAsTable';

export default class EditSLAform extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: this.props.theme
      };
   }

   setSelectTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const { theme, projectName } = this.state;

      return (
         <div>
            {<SLATable theme={theme} name={projectName} edit remove retire />}
         </div>
      );
   }
}
