import React, { Component } from 'react';
import { Button, Tab, Tabs } from '@blueprintjs/core';
import MainContainer from '../MainContainer/MainContainer';
//import '../NavBar/Navbar.css'
import './Sidebar.css';

export default class Sidebar extends Component {
   constructor(props) {
      super(props);
      this.state = {
         selected: 0
      };
      this.changeSelection = this.changeSelection.bind(this);
   }

   changeSelection(id) {
      const i = id;
      this.setState({ selected: i });
   }

   render() {
      return (
         <div>
            <nav class='side-bar no-border'>
               <Button
                  minimal
                  large
                  icon='home'
                  fill
                  active={this.state.selected === 0}
                  intent={this.state.selected === 0 ? 'primary' : null}
                  onClick={this.changeSelection}
               ></Button>
               <Button
                  minimal
                  large
                  icon='dashboard'
                  fill
                  active={this.state.selected === 1}
                  intent={this.state.selected === 1 ? 'primary' : null}
                  onClick={this.changeSelection}
               ></Button>
               <Button minimal large icon='control' fill></Button>
               <Button minimal large icon='edit' fill></Button>
               <Button minimal large icon='locate' fill></Button>
            </nav>

            <MainContainer>{this.state.selected}</MainContainer>
         </div>
      );
   }
}
