import React, { Component } from 'react';
import { Table, Button, ButtonGroup } from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free';

export default class ProjectTable extends Component {
   state = {
      theme: this.props.theme,
      data: this.props.data,
      filterName: this.props.name,
      _testDataSet: [
         {
            id: 1,
            name: 'L&D',
            staus: 'Rafal Cymbalista',
            deliveryType: '123123',
            sm: 'Manager',
            ops: 'English',
            cim: '123',
            ras: '123'
         },
         {
            id: 1,
            name: 'L&D',
            staus: 'Rafal Cymbalista',
            deliveryType: '123123',
            sm: 'Manager',
            ops: 'English',
            cim: '123',
            ras: '123'
         }
      ]
   };

   /*TODO:
      -- Function to strip the data into the table values
         -- data structure : Headers + values
   */

   setTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'table-dark';
      }
      if (theme === 'light') {
         return '';
      }
   }

   renderTableHeaders() {
      return (
         <tr>
            <th>Name</th>
            <th>Status</th>
            <th>Delivery Type</th>
            <th>SM</th>
            <th>OPS</th>
            <th>CIM</th>
            <th>RAS</th>
            <th />
         </tr>
      );
   }

   renderTableData() {
      return this.state._testDataSet.map((project, index) => {
         const { id, name, status, deliveryType, sm, ops, cim, ras } = project;
         return (
            <tr key={id}>
               <td>{name}</td>
               <td>{status}</td>
               <td>{deliveryType}</td>
               <td>{sm}</td>
               <td>{ops}</td>
               <td>{cim}</td>
               <td>{ras}</td>
               <td>
                  <ButtonGroup>
                     <Button size='sm'>
                        <span className='fas fa-trash-alt' />
                     </Button>
                     <Button size='sm'>
                        <span className='fas fa-edit' />
                     </Button>
                  </ButtonGroup>
               </td>
            </tr>
         );
      });
   }

   render() {
      return (
         <div>
            <p>All Available Projects</p>
            <Table
               responsive
               borderless
               striped
               hover
               size='sm'
               className={this.setTheme()}
            >
               <thead>{this.renderTableHeaders()}</thead>
               <tbody>{this.renderTableData()}</tbody>
            </Table>
         </div>
      );
   }
}
