import React, { Component } from 'react';
import { TabContent, TabPane } from 'reactstrap';

import TopBar from '../Nav/TopBar/TopBar';
import LeftBar from '../Nav/LeftBar/LeftBar';
import NavItem from '../Nav/NavItem/NavItem';
import MainContainer from '../MainContainer/MainContainer';
import ProjectCard from '../Card/ProjectCard/ProjectsManagerCard';
import UIManager from '../../Modules/UIManager/UIManager';
import SLACard from '../Card/SLACard/SLACard';
import EmployeeCard from '../Card/EmployeeCard/EmployeeCard';

export default class MainProjects extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         showReportFilter: false,
         activeTab: '1'
      };
   }

   setBgByTheme(theme) {
      if (theme === 'dark') {
         return 'main-data-bg-dark';
      }
      if (theme === 'light') {
         return 'main-data-bg-light';
      } else {
         return 'main-data-bg-dark';
      }
   }

   switchTab(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   switchShowReportFilter = () => {
      this.setState(state => ({ showReportFilter: !state.showReportFilter }));
   };

   render() {
      const { theme } = this.state;

      return (
         <MainContainer theme={theme}>
            <TopBar theme={theme}>
               <NavItem
                  icon='fas fa-filter'
                  onClick={this.switchShowReportFilter}
               />
            </TopBar>
            <LeftBar theme={theme}>
               <NavItem
                  icon='fas fa-project-diagram'
                  active={this.state.activeTab === '1'}
                  onClick={() => {
                     this.switchTab('1');
                  }}
               />
               <NavItem
                  icon='fa fa-tasks'
                  active={this.state.activeTab === '2'}
                  onClick={() => {
                     this.switchTab('2');
                  }}
               />
               <NavItem
                  icon='fa fa-users'
                  active={this.state.activeTab === '3'}
                  onClick={() => {
                     this.switchTab('3');
                  }}
               />
               <NavItem
                  icon='fa fa-edit'
                  active={this.state.activeTab === '4'}
                  onClick={() => {
                     this.switchTab('4');
                  }}
               />
            </LeftBar>
            <TabContent activeTab={this.state.activeTab} className='mb-5'>
               <TabPane tabId='1'>
                  <ProjectCard theme={theme} />
               </TabPane>
               <TabPane tabId='2'>
                  <SLACard theme={theme} />
               </TabPane>
               <TabPane tabId='3'>
                  <EmployeeCard theme={theme} />
               </TabPane>
               <TabPane tabId='4'>Review notes</TabPane>
            </TabContent>
         </MainContainer>
      );
   }
}
