class User {
   /**
    * @returns {String} User Object.Id
    */
   static getUserId() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user._id;
   }

   /**
    * @returns {String} Username
    */
   static getName() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.name;
   }

   /**
    * @returns {String} UserType ['user','team leader','service manager','operations manager','general manager','admin','root']
    */
   static getUserType() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.usertype.userType;
   }

   /**
    * @returns {String} UserType ['user','team leader','service manager','operations manager','general manager','admin','root']
    *          AS Object.Id
    */
   static getUserTypeId() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.usertype._id;
   }

   /**
    * @returns {String} User SAP ID Number
    */
   static getSAP() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.sap;
   }

   /**
    * @returns {String} User Employee Role
    */
   static getRole() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.role.role;
   }

   /**
    * @returns {String} User Employee Role Object.Id
    */
   static getRoleId() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.role._id;
   }

   /**
    * @returns {String} User Employee Email address
    */
   static getEmployeeEmail() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.email;
   }

   /**
    * @returns {String} User Email address
    */
   static getUserEmail() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.email;
   }

   /**
    * @returns {String} User Employee Project Name
    */
   static getProject() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.project.name;
   }

   /**
    * @returns {String} User Employee Project Object.id
    */
   static getProjectID() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.project._id;
   }

   /**
    * @returns {String} User Employee RM name
    */
   static getRMName() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.rm.name;
   }

   /**
    * @returns {String} User Employee RM Object.Id
    */
   static getRMId() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.rm._id;
   }

   /**
    * @returns {String} User Employee RM email address
    */
   static getRMemail() {
      var user = JSON.parse(localStorage.getItem('user'));
      return user.employeeInfo.rm.email;
   }
}

export default User;
