import React, { Component } from "react";
import axios from "axios";
import StdCard from "../Card/StdCard";
import Select from "../Buttons/Select/Select";
import {
   Row,
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   Label,
   Input
} from "reactstrap";

export default class CreateUser extends Component {
   state = {
      theme: this.props.theme,
      userType: "",
      username: "",
      email: "",
      password: "",
      project: "",
      projects: [
         {
            name: "GDC",
            value: "GDC"
         }
      ],
      created: false,
      message: "",
      isLoading: ""
   };

   onSubmit = e => {
      e.preventDefault();
      const { message, password, created, email, project } = this.state;

      const URL = "http://localhost:3001/api/users/create/";

      const data = {
         user: {
            email: email,
            password: password
         }
      };

      axios
         .post(URL, data)
         .then(res => {
            this.setState({
               created: res.data.created
            });
            if (res.data.created === "ok") {
               alert("User created successfully");
            }
         })
         .catch(err => {
            this.setState({
               created: "error",
               message: "error"
            });
         });
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   setInputTheme() {
      const { theme } = this.props;
      if (theme === "dark") {
         return "table-input-dark";
      }
      if (theme === "light") {
         return "";
      }
   }
   setSelectTheme() {
      const { theme } = this.props;
      if (theme === "dark") {
         return "my-select-dark";
      }
      if (theme === "light") {
         return "";
      }
   }

   render() {
      const {
         message,
         email,
         password,
         username,
         userType,
         project
      } = this.state;

      return (
         <StdCard
            headerText="Create new user"
            icon="fas fa-user"
            theme={this.props.theme}
         >
            <Form>
               <Row>
                  <Col md={12}>
                     {message !== "" && (
                        <Alert className="bg-transparent border border-warning text-warning rounded-corners text-center">
                           {message}
                        </Alert>
                     )}
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="project">User Type</Label>
                        <Input
                           type="select"
                           name="userType"
                           id="userType"
                           size="sm"
                           value={userType}
                           onChange={this.onChange}
                           placeholder="Please provide username"
                           className={this.setSelectTheme()}
                        >
                           <option>Standard</option>
                           <option>Admin</option>
                           <option>Root</option>
                        </Input>
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="project">Project</Label>
                        <Input
                           type="select"
                           name="project"
                           id="project"
                           size="sm"
                           value={project}
                           onChange={this.onChange}
                           placeholder="Please provide username"
                           className={this.setSelectTheme()}
                        >
                           <option>GDC</option>
                           <option>P1</option>
                           <option>P2</option>
                        </Input>
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="email">Username</Label>
                        <Input
                           type="text"
                           name="username"
                           id="username"
                           size="sm"
                           value={username}
                           onChange={this.onChange}
                           placeholder="Please provide username"
                           className={this.setInputTheme()}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="email">Email</Label>
                        <Input
                           type="email"
                           name="email"
                           id="exampleEmail"
                           size="sm"
                           placeholder="Please provide email"
                           value={email}
                           onChange={this.onChange}
                           className={this.setInputTheme()}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={12}>
                     <FormGroup>
                        <Label for="password">Password</Label>
                        <Input
                           type="password"
                           name="password"
                           id="password"
                           size="sm"
                           placeholder="Please provide temporary password"
                           value={password}
                           onChange={this.onChange}
                           className={this.setInputTheme()}
                        />
                     </FormGroup>
                  </Col>
               </Row>
               <div className="text-right">
                  <Button color="primary" onClick={this.onSubmit}>
                     Create
                  </Button>
               </div>
            </Form>
         </StdCard>
      );
   }
}
