import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import { Card } from '@blueprintjs/core';
import UIManager from '../../../Modules/UIManager/UIManager';

export default class LineChart extends Component {
   constructor(props) {
      super(props);

      this.state = {
         options: {
            chart: {
               id: 'basic-bar',
               background: 'transparent',
               dropShadow: {
                  enabled: true,
                  color: '#000',
                  top: 18,
                  left: 7,
                  blur: 10,
                  opacity: 0.2
               },
               foreColor:
                  UIManager.getTheme() === 'dark' ? '#f5f8fa' : '#1e1e1e',
               toolbar: false
            },
            dataLabels: {
               enabled: true,
               style: {
                  colors: ['#304758']
               }
            },
            stroke: {
               curve: 'smooth'
            },
            title: {
               text: 'Some Data',
               align: 'left'
            },
            grid: {
               borderColor: 'rbga(0,0,0,0.5)',
               row: {
                  colors: ['rgba(0,0,0,0.2)', 'transparent'], // takes an array which will be repeated on columns
                  opacity: 0.5
               }
            },
            xaxis: {
               type: 'category',
               title: {
                  text: 'Year'
               },
               labels: {
                  style: {
                     cssClass: 'text'
                  }
               },

               categories: [
                  1991,
                  1992,
                  1993,
                  1994,
                  1995,
                  1996,
                  1997,
                  1998,
                  1999
               ],
               axisBorder: {
                  show: true
               },
               tooltip: {
                  enabled: false
               }
            },
            yaxis: {
               title: {
                  text: 'Values'
               },
               min: 5,
               max: 150
            },
            annotations: {
               position: 'back',
               yaxis: [
                  {
                     y: 50,
                     y2: null,
                     strokeDashArray: 3,
                     borderColor: 'red',
                     fillColor: '#c2c2c2',
                     opacity: 0.8,
                     offsetX: 0,
                     offsetY: -3,
                     yAxisIndex: 0,
                     label: {
                        borderColor: 'transparent',
                        style: {
                           color: '#fff',
                           background: 'red'
                        },
                        text: 'Target'
                     }
                  }
               ]
            },
            legend: {
               position: 'top',
               horizontalAlign: 'right',
               floating: true,
               offsetY: -25,
               offsetX: -5
            }
         },
         series: [
            {
               name: 'series-1',
               data: [30, 40, 45, 50, 49, 60, 70, 91]
            },
            {
               name: 'series-2',
               data: [10, 20, 30, 100, 120, 20, 10, 5]
            }
         ]
      };
   }
   render() {
      return (
         <Card>
            <div className='card-body text'>
               <Chart
                  options={this.state.options}
                  series={this.state.series}
                  type='line'
                  width='100%'
                  height={350}
               />
            </div>
         </Card>
      );
   }
}
