import React, { Component } from 'react'
import '@fortawesome/fontawesome-free/css/all.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import './Quic.css'
import ScoreCard from './ScoreCard/ScoreCard';

export default class Quick extends Component {

   state = {
      headcount: {
         headcount: 0,
         headcountProc: 0,
         headcountTrend: 0,
         eHeadcount: 0,
         eHeadcountProc: 0,
         eHeadcountTrend: 0
      },
      productivity: {
         productivity: 0,
         productivityProc: 0,
         productivityTrend: 0,
         eProductivity: 0,
         eProductivityProc: 0,
         eProductivityTrend: 0
      },
      staff: {
         availability: 0,
         availabilityProc: 0,
         availabilityTrend: 0,
         shrinkage: 0,
         shrinkageProc: 0,
         shrinkageTrend: 0
      }
   }

   componentDidMount() {
      this.getHeadcount();
   }

   getHeadcount() {
      fetch('/api/prodsummary')
         .then(res => res.json())
         .then(({ 
            headcount,
            headcountProc,
            eHeadcount,
            eHeadcountProc,
            productivity,
            productivityProc,
            eProductivity,
            eProductivityProc,
            availability,
            availabilityProc,
            shrinkage,
            shrinkageProc
         }) => this.setState({ 
            headcount,
            headcountProc,
            eHeadcount,
            eHeadcountProc,
            productivity,
            productivityProc,
            eProductivity,
            eProductivityProc,
            availability,
            availabilityProc,
            shrinkage,
            shrinkageProc
         }))
   }

   render() {
      console.log(this.state);
      return (
         <div className="row">
            <ScoreCard icon="fa fa-users" title="Headcount" procValue={this.state.headcountProc} numberValue={this.state.headcount} trend="0"></ScoreCard>
            <ScoreCard icon="fa fa-user" title="E.Headcount" procValue={this.state.eHeadcountProc} numberValue={this.state.eHeadcount} trend="-1"></ScoreCard>
            <ScoreCard icon="fa fa-chart-line" title="Productivity" procValue={this.state.productivityProc} numberValue={this.state.productivity} trend="1"></ScoreCard>
            <ScoreCard icon="fa fa-chart-line" title="E. Productivity" procValue={this.state.eProductivityProc} numberValue={this.state.eProductivity} trend="1"></ScoreCard>
            <ScoreCard icon="fa fa-clock" title="Availability" procValue={this.state.availabilityProc} numberValue={this.state.availability} trend="-1"></ScoreCard>
            <ScoreCard icon="fa fa-stopwatch" title="Shrinkage" procValue={this.state.shrinkageProc} numberValue={this.state.shrinkage} trend="0"></ScoreCard>
         </div>
      )
   }
}
