import React, { Component } from 'react';

export default class NavItem extends Component {
   activate(enable) {
      if (enable) return 'active';
      return '';
   }

   render() {
      const { icon, active, href } = this.props;

      return (
         <li className='nav-item'>
            <a
               className={`nav-link nav-icon ${this.activate(active)}`}
               aria-haspopup='true'
               aria-expanded='false'
               href={href}
            >
               <span className={icon} onClick={this.props.onClick} />
            </a>
         </li>
      );
   }
}
