import React, { Component } from 'react';
import Sidebar from '../Sidebar/Sidebar';
import SidebarItem from '../Sidebar/SidebarItem';
import SidebarItemSpecial from '../Sidebar/SidebarItemSpecial';
import Client from '../../Modules/Client/Client';
import UIManager from '../../Modules/UIManager/UIManager';
import { MenuDivider, Navbar, Alignment, Icon } from '@blueprintjs/core';
import { Redirect } from 'react-router-dom';

export default class AppSideBar extends Component {
   constructor(props) {
      super(props);
      this.state = {
         selected: 'home',
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark',
         logout: false,
         goHome: false,
         isOpen: false
      };
      this.switchTheme = this.switchTheme.bind(this);
      this.handleLogout = this.handleLogout.bind(this);
   }

   changeSelection(id) {
      this.setState({ selected: id });
   }

   switchTheme() {
      UIManager.swtichTheme();
      UIManager.setChartView();
      this.setState({
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark'
      });
   }

   handleLogout = e => {
      e.preventDefault();
      Client.logout();
      this.setState({
         logout: true
      });
   };

   render() {
      const { selected } = this.state;

      if (this.state.logout) {
         return <Redirect to='/login' />;
      }

      return (
         <Sidebar>
            <div className='side-bar-logo bp3-fill'>
               <i class='fas fa-dice-d20'></i>
            </div>
            <MenuDivider />
            <div className='side-bar-group-center'>
               <SidebarItem
                  to='/home'
                  icon='home'
                  tooltip='Home'
                  selected={selected === 'home'}
                  onClick={() => this.changeSelection('home')}
               />
               <SidebarItem
                  to='/KPIs'
                  icon='locate'
                  tooltip='KPIs'
                  selected={selected === 'KPIs'}
                  onClick={() => this.changeSelection('KPIs')}
               />
               <SidebarItem
                  to='/Dashboards'
                  icon='dashboard'
                  tooltip='Dashboard'
                  selected={selected === 'dashboard'}
                  onClick={() => this.changeSelection('dashboard')}
               />
               <SidebarItem
                  to='/Employees'
                  icon='user'
                  tooltip='Employees'
                  selected={selected === 'employees'}
                  onClick={() => this.changeSelection('employees')}
               />
               <SidebarItem
                  to='/Projects'
                  icon='gantt-chart'
                  tooltip='Projects'
                  selected={selected === 'projects'}
                  onClick={() => this.changeSelection('projects')}
               />
               <SidebarItem
                  to='/Teams'
                  icon='people'
                  tooltip='Teams'
                  selected={selected === 'teams'}
                  onClick={() => this.changeSelection('teams')}
               />
               <SidebarItem
                  to='/Reports'
                  icon='manually-entered-data'
                  tooltip='Reports'
                  selected={selected === 'reports'}
                  onClick={() => this.changeSelection('reports')}
               />
               <SidebarItem
                  to='/Settings'
                  icon='cog'
                  tooltip='Settings'
                  selected={selected === 'settings'}
                  onClick={() => this.changeSelection('settings')}
               />
               <SidebarItem
                  to='/AdminPanel'
                  icon='control'
                  tooltip='Admin Panel'
                  selected={selected === 'admin'}
                  onClick={() => this.changeSelection('admin')}
               />
            </div>

            <div className='side-bar-group-bottom'>
               <MenuDivider />
               <SidebarItem
                  icon={this.state.themeIcon}
                  intent=''
                  tooltip='Switch theme'
                  onClick={this.switchTheme}
               />
               <SidebarItemSpecial
                  icon='power'
                  intent='danger'
                  tooltip='Logout'
                  onClick={this.handleLogout}
               />
            </div>
         </Sidebar>
      );
   }
}
