import React, { Component } from 'react';
import { HTMLSelect } from '@blueprintjs/core';
import Client from '../../Modules/Client/Client';

export default class EmployeesOptions extends Component {
   state = {
      options: [],
      option: ''
   };
   componentDidMount() {
      this.getOptions();
   }
   async getOptions() {
      try {
         var result = await Client.Services.UsersService.getUsers();
         this.setState({
            options: result.data
         });
      } catch (err) {
         this.setState({ options: new Array('N/A'), option: 'N/A' });
      }
   }

   renderOptions() {
      if (this.state.options) {
         return this.state.options.map((option, key) => {
            return (
               <option key={key} value={option._id}>
                  ${option.username} | ${option.email}
               </option>
            );
         });
      }
   }
   render() {
      return (
         <HTMLSelect
            id={this.props.id}
            ref={this.props.ref}
            onChange={this.props.onChange}
            disabled={this.props.disabled}
            fill={this.props.fill}
            value={this.props.selected}
         >
            <option hidden disabled>
               {this.props.placeholder}
            </option>
            {this.renderOptions()}
         </HTMLSelect>
      );
   }
}
